UPDATE dbscript_string SET content_loc3='Danke, $N Und viel Glück!' WHERE entry=2000000000; /* content: Thank you, $N. And luck to you. */
UPDATE dbscript_string SET content_loc3='Die Klippenquellfälle befinden sich in den Bergen Richtung Osten und Norden.' WHERE entry=2000000001; /* Cliffspring Falls lies along the mountains to the east and north. */
UPDATE dbscript_string SET content_loc3='Geht rasch, $N Mein Schicksal liegt in Eurer Hand.' WHERE entry=2000000002; /* Go swiftly $N. My fate is in your hands. */
UPDATE dbscript_string SET content_loc3='Großmutter Falkenwind deutet auf die Wasserkanne, die auf dem Rand des Brunnens steht.' WHERE entry=2000000003; /* %s gestures to the pitcher of water sitting on the edge of the well. */
UPDATE dbscript_string SET content_loc3='Es ist lange her, Bethor, mein Freund.' WHERE entry=2000000004; /* It has been a long time, Bethor, my friend. */
UPDATE dbscript_string SET content_loc3='Wenn es die Zeit erlaubt, dann müssen wir uns in Ruhe unterhalten, denn wir haben viel zu besprechen.' WHERE entry=2000000005; /* When time permits, we must speak at length. For we have much to discuss. */
UPDATE dbscript_string SET content_loc3='Und danke, $N Ohne Eure Hilfe hätte ich den Weg zu den Verlassenen nie gefunden.' WHERE entry=2000000006; /* And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE dbscript_string SET content_loc3='Lebt wohl, mein Freund.' WHERE entry=2000000007; /* Farewell, my friend. */
UPDATE dbscript_string SET content_loc3='%s schnuppert an dem Elixier und langt dann eifrig zu!' WHERE entry=2000000008; /* %s sniffs the elixir then eagerly digs in! */
UPDATE dbscript_string SET content_loc3='Ah, das ist genau richtig! Ich glaube, ich hole mir noch ein paar... könnt Ihr für mich auf diese Fässer aufpassen, $N' WHERE entry=2000000012; /* Ah, that sure does hit the spot! I think i\'ll get myself a couple more...can you watch these barrels for me, $N? */
UPDATE dbscript_string SET content_loc3='Und wie läuft das Fassbewachen...?' WHERE entry=2000000013; /* How goes the barrel watching...? */
UPDATE dbscript_string SET content_loc3='Nun ja, ich muss mich wieder um die Arbeit kümmern. Aber so eine kleine Pause tut schon gut und so ein Donnerbräu Lager schmeckt immer!' WHERE entry=2000000014; /* Well, back to business for me. But it sure was nice taking that short break, and it\'s always nice drinking Thunder Ale! */
UPDATE dbscript_string SET content_loc3='Ich danke Euch, $n. Und mein Bruder dankt Euch ebenfalls.' WHERE entry=2000000016; /* I thank you. $n. And my brother thanks you. */
UPDATE dbscript_string SET content_loc3='%s beginnt zu singen, während er die Quellsteine und das Bernsteinkorn vor dem Stammesfeuer vermischt.' WHERE entry=2000000017; /* %s begins chanting as he mixes the well stones and ambercom before the Tribal Fire. */
UPDATE dbscript_string SET content_loc3='Das Wasser der Seher steht für Euch zum Trinken bereit, $N' WHERE entry=2000000018; /* The Water of the Seers is ready for your consumption, $N. */
UPDATE dbscript_string SET content_loc3='Habt Geduld, $N. Die Fackel ist fast fertig.' WHERE entry=2000000019; /* Be patient $N. The torch is almost complete. */
UPDATE dbscript_string SET content_loc3='Das ist interessant...' WHERE entry=2000000020; /* That\'s interesting... */
UPDATE dbscript_string SET content_loc3='Ich muss Noram und Horatio kontaktieren.' WHERE entry=2000000021; /* I need to consult Noram and Horatio. */
UPDATE dbscript_string SET content_loc3='Geht jetzt, $N. Findet Volcor... und denkt daran, dass die Magie, die Euch beschützt, versagen wird, wenn Ihr mit anderen mehr tut als bloß zu reden.' WHERE entry=2000000022; /* Go now, $N. Find Volcor... and remember, the magics protecting you will fail if you interact with anyone, save for talking. */
UPDATE dbscript_string SET content_loc3='%s beginnt, einen Zauber zu wirken, als Ihr ihm den Umhang abnehmt.' WHERE entry=2000000023; /* %s begins to cast a spell as you take the cloak from him. */
UPDATE dbscript_string SET content_loc3='M... m... mir... geht... es... nicht... gut...' WHERE entry=2000000024; /* %s I. . .I. . .don\'t. . .feel. . .right. . . */
UPDATE dbscript_string SET content_loc3='Mein Verstand... mein Fleisch... ich... verwese...!' WHERE entry=2000000025; /* My mind. . .my flesh. . .I\'m. . .rotting. . . .! */
UPDATE dbscript_string SET content_loc3='Ups!' WHERE entry=2000000026; /* Whoops! */
UPDATE dbscript_string SET content_loc3='Das Volk von Westfall salutiert vor $N, einem mutigen und tapferen Verteidiger der Freiheit.' WHERE entry=2000000028; /* The People of Westfall salute $N, a brave and valiant defender of freedom. */
UPDATE dbscript_string SET content_loc3='Das macht Sinn!' WHERE entry=2000000030; /* This makes sense! */
UPDATE dbscript_string SET content_loc3='Vorsicht, $n! Schaut nach Westen!' WHERE entry=2000000031; /* Beware, $n! Look to the west! */
UPDATE dbscript_string SET content_loc3='Seid gewappnet, $N, denn der Dämon in dieser Kugel ist eine Teufelsbestie.' WHERE entry=2000000032; /* Gird yourself $N, for the demon in this orb is a fel beast. */
UPDATE dbscript_string SET content_loc3='%s beschwört einen Boten...' WHERE entry=2000000033; /* %s summons a messenger... */
UPDATE dbscript_string SET content_loc3='Lass mich dir dabei helfen.' WHERE entry=2000000034; /* Let me help you with those. */
UPDATE dbscript_string SET content_loc3='Trink das, es wird dir helfen' WHERE entry=2000000035; /* Drink this, it will help. */
UPDATE dbscript_string SET content_loc3='Dir wird es gut gehen' WHERE entry=2000000036; /* You\'re going to be just fine. */
UPDATE dbscript_string SET content_loc3='Bis wir uns wiedersehen, tapfere Seele.' WHERE entry=2000000042; /* Until we meet again, brave one. */
UPDATE dbscript_string SET content_loc3='Es ist vollbracht...' WHERE entry=2000000044; /* It is done... */
UPDATE dbscript_string SET content_loc3='Es geht doch nichts über kochend heißes Morgengebräu an einem kalten Morgen in Dun Morogh! Danach kann der Tag kommen!' WHERE entry=2000000045; /* There\'s nothing like some scalding mornbrew on a chilly Dun Morogh day to get things started right! */
UPDATE dbscript_string SET content_loc3='Ooooh! Heiß! Heiß! Heiß! Wenn Euch das nicht auf Touren bringt, kann ich Euch auch nicht helfen!' WHERE entry=2000000046; /* Oooooo hot hot hot! If that won\'t put spring IN your step,I don\'t know what will! */
UPDATE dbscript_string SET content_loc3='Verflixt! Nein!!! Das kann... verflixt... nicht sein! Ihr habt mich... verflixt... mit Euren faulen Tricks betrogen! Verflixt!' WHERE entry=2000000047; /* Ribbit! No!! This cannot... ribbit... be! You have duped me with... ribbit... your foul trickery! Ribbit! */
UPDATE dbscript_string SET content_loc3='Lasst uns nun diese seltene Erde in meinen Pflanzbehälter füllen...' WHERE entry=2000000048; /* Now let us place this rare earth in my planter... */
UPDATE dbscript_string SET content_loc3='Ich danke Euch nochmals, $N. Diese seltene Erde wird mir bei meinen Experimenten von großem Nutzen sein.' WHERE entry=2000000049; /* Thank you again, $N. This rare earth will be very helpful in my experiments. */
UPDATE dbscript_string SET content_loc3='Hey, Belm! Gib mir einen Becher Donnerbräu Lager und einen für meinen guten Freund $N.' WHERE entry=2000000056; /* Hey there, Belm! Give me a mug of Thunder Ale, and one for my good friend $N. */
UPDATE dbscript_string SET content_loc3='Geister des Wassers, ich preise euch und erflehe einen Gefallen von euch. Ihr habt die Bitten gehört, die $N an euch gerichtet hat, und ich bin mir sicher, ihre Beweggründe sind lauter und edel. Bitte, helft uns.' WHERE entry=2000000057; /* Spirits of Water, I give you praise, and I beg of you a favour. You have heard $N\'s please, and I trust his inent is noble. Please, will you aid us? */
UPDATE dbscript_string SET content_loc3='Dank Euch, großer Geist. Danke!' WHERE entry=2000000058; /* Thank you great spirit. Thank you! */
UPDATE dbscript_string SET content_loc3='Ihr seid entlassen, $n.' WHERE entry=2000000059; /* You are dismissed, $N. */
UPDATE dbscript_string SET content_loc3='Wir sind verdammt!' WHERE entry=2000000060; /* We\'re doomed! */
UPDATE dbscript_string SET content_loc3='Folgt mir, guter $C. Ich werde Euer Pferdegeschirr schneller fertigen, als ich ein Zwergen - Starkbier hinunterspülen kann. Mmmm.... Starkbier.' WHERE entry=2000000063; /* Follow me, good $C. I\'ll have your barding done faster than I could down a Dwarven stout. Mmmm... stout. */
UPDATE dbscript_string SET content_loc3='Nun, das sollte ausreichen. Komm jetzt, lasst uns zurück zum Laden gehen, um das Geschäft zu beenden!' WHERE entry=2000000064; /* Well, that should do it. Come now, back to the shop to finish our business! */
UPDATE dbscript_string SET content_loc3='Wir werden keinen Dämonen-Diener in unseren Ländern dulden!' WHERE entry=2000000065; /* We will suffer no demon\'s servant in our lands! */
UPDATE dbscript_string SET content_loc3='Friede und Geduld sei mit Euch, $N. Bleibt immer stark.' WHERE entry=2000000066; /* Peace and patience be with you, $N. Remain strong always. */
UPDATE dbscript_string SET content_loc3='%s versucht, Maurys Klumpfuß zu öffnen...' WHERE entry=2000000067; /* %s tries opening Maury\'s Clubbed Foot... */
UPDATE dbscript_string SET content_loc3='Pah! $N, dieser Fuß gibt nicht nach!' WHERE entry=2000000068; /* Bah! $N, this foot won\'t budge! */
UPDATE dbscript_string SET content_loc3='Danke, $N.' WHERE entry=2000000069; /* Thank you, $N. */
UPDATE dbscript_string SET content_loc3='Ich bin sicher, dass ich eine Verwendung für diese Mixtur finden werde...' WHERE entry=2000000070; /* I\'m sure I can find a use for this mixture... */
UPDATE dbscript_string SET content_loc3='Nochmals vielen Dank für Ihre Hilfe. Möge der Wind während Ihrer Reisen immer in Ihrem Rücken sein' WHERE entry=2000000071; /* Thank you again for your aid. May the wind always be at your back during your travels. */
UPDATE dbscript_string SET content_loc3='Das war einfacher als ich dachte... das Siegelzeichen von Thoradin ist endlich wieder ganz.' WHERE entry=2000000072; /* Easier than I expected... the sigil of Thoradin is whole, once again. */
UPDATE dbscript_string SET content_loc3='Wartet, $N!!! Es sieht so aus, als würden wir hier Hilfe brauchen...' WHERE entry=2000000073; /* Wait, $N!!! It looks like we\'re going to need some help here... */
UPDATE dbscript_string SET content_loc3='In den Kasten, meine Süßen! Genau so. Einer nach dem anderen.' WHERE entry=2000000074; /* Into the box me pretties! Thas it. One by one, ye go. */
UPDATE dbscript_string SET content_loc3='Hört her, Bewohner von Tanaris! Ihr sollt wissen, dass $n ein erhabenes Mitglied des Mithrilordens ist. Ein Schmied von Ehre, Hingabe und unendlicher Geduld. Ein dreifaches Hoch auf $n!' WHERE entry=2000000076; /* Hear ye, denizens of Tanaris! Let it be known $n is an exalted member of the Mithril Order. A blacksmith of honor, dedication, and infinite patience. Three cheers for $n */
UPDATE dbscript_string SET content_loc3='%s spürt, wie sein Blut in Wallung gerät und die Arglist des Raptors ihn durchströmt.' WHERE entry=2000000077; /* %s watches as his blood begins to stir, and the guile of the raptor slips through him. */
UPDATE dbscript_string SET content_loc3='Was ist das für eine Stärke, die mich durchfließt? Ist dies die Macht des Schamanismus, von der Torgan gesprochen hat...?' WHERE entry=2000000078; /* What is this power that flows through me? Is this the power of shamanism that Tor\'gan spoke of...? */
UPDATE dbscript_string SET content_loc3='Beeindruckend! Ich fühle mich... erneuert. Meine Kraft kehrt zurück!' WHERE entry=2000000079; /* Amazing! I feel... renewed. My strength returns! */
UPDATE dbscript_string SET content_loc3='%s beginnt einen Ritus der Erschaffung...' WHERE entry=2000000080; /* %s begins a rite of creation... */
UPDATE dbscript_string SET content_loc3='Ich habe das Totem erschaffen. Ihr, $N, müsst den Brunnen läutern.' WHERE entry=2000000081; /* I have created the totem. You, $N, must cleanse the well. */
UPDATE dbscript_string SET content_loc3='%s erschafft ein neues Totem...' WHERE entry=2000000082; /* %s creates a new totem... */
UPDATE dbscript_string SET content_loc3='Das Totem ist fertig, $N.' WHERE entry=2000000083; /* The totem is made, $N. */
UPDATE dbscript_string SET content_loc3='%s verwandelt die Gegenstände von $n in ein Totem der Läuterung...' WHERE entry=2000000084; /* %s transforms the items $N gave him into a totem of cleansing... */
UPDATE dbscript_string SET content_loc3='Das Totem ist fertig. Der Brunnen kann nun geläutert werden.' WHERE entry=2000000085; /* The totem is made. The well may now be cleansed. */
UPDATE dbscript_string SET content_loc3='%s betrachtet das Pergament aus dem Folianten der Schattenhammerkultisten...' WHERE entry=2000000086; /* %s studies the parchment from the Twilight Tome... */
UPDATE dbscript_string SET content_loc3='%s wird von freigesetzter Magie getroffen!' WHERE entry=2000000087; /* %s is struck by unleashed magic! */
UPDATE dbscript_string SET content_loc3='Hmmm...' WHERE entry=2000000088; /* Hmm... */
UPDATE dbscript_string SET content_loc3='Die Geister des Steinkrallengebirges wüten noch immer, $n.' WHERE entry=2000000089; /* The spirits of Stonetalon still rage, $N. */
UPDATE dbscript_string SET content_loc3='Ich fürchte, wir werden sie niemals beschwichtigen können...' WHERE entry=2000000090; /* I fear we may never soothe them... */
UPDATE dbscript_string SET content_loc3='%s hört sich die Bitte von $n an.' WHERE entry=2000000091; /* %s listens to $N\'s plea. */
UPDATE dbscript_string SET content_loc3='Ihr habt gut daran getan zu kommen. Meine Weisheit gehört Euch.' WHERE entry=2000000092; /* You were right to come. My wisdom will be yours. */
UPDATE dbscript_string SET content_loc3='Geht, mein Diener. Bringt Kunde vom Steinkrallengebirge zu Apothekerin Zamah.' WHERE entry=2000000093; /* Go, my servant. Send word of Stonetalon to Apothecary Zamah. */
UPDATE dbscript_string SET content_loc3='%s übergibt Apothekerin Zamah eine Nachricht.' WHERE entry=2000000094; /* %s gives message to Apothecary Zamah. */
UPDATE dbscript_string SET content_loc3='%s lächelt.' WHERE entry=2000000095; /* %s smiles. */
UPDATE dbscript_string SET content_loc3='%s erschafft sein Toxin...' WHERE entry=2000000096; /* %s creates his toxin... */
UPDATE dbscript_string SET content_loc3='Da wären wir, $n. Das Toxin steht bereit.' WHERE entry=2000000097; /* There we are, $N. The toxin is ready. */
UPDATE dbscript_string SET content_loc3='%s füllt einen Behälter mit dem Toxin.' WHERE entry=2000000098; /* %s fills a container with the toxin. */
UPDATE dbscript_string SET content_loc3='Das Toxin ist fertig, $n.' WHERE entry=2000000099; /* %s The toxin is ready, $N. */
UPDATE dbscript_string SET content_loc3='%s stellt die Kiste mit den Teilen auf den Boden.' WHERE entry=2000000100; /* %s places the crate of parts on the ground. */
UPDATE dbscript_string SET content_loc3='%s greift sich ein Teil und steckt es in die Tasche...' WHERE entry=2000000101; /* %s grabs a part and puts it in his pocket... */
UPDATE dbscript_string SET content_loc3='So, das sollte reichen...' WHERE entry=2000000102; /* There, that should do it... */
UPDATE dbscript_string SET content_loc3='%s saugt etwas Traumstaub in die Nase...' WHERE entry=2000000103; /* %s snorts a speck of dream dust up his nose... */
UPDATE dbscript_string SET content_loc3='%s ist benommen...' WHERE entry=2000000104; /* %s is dazed... */
UPDATE dbscript_string SET content_loc3='HATSCHI!' WHERE entry=2000000105; /* ACHOO! */
UPDATE dbscript_string SET content_loc3='Unglaublich! $n, dieser Traumstaub ist aber ein Teufelszeug!' WHERE entry=2000000106; /* Wow! $N, that dream dust is powerful stuff! */
UPDATE dbscript_string SET content_loc3='He, $n! Vergesst nicht, eine Kiste, ein Handbuch und einen Leitstecken mitzunehmen!' WHERE entry=2000000107; /* Hey $N! Don\'t forget to grab a crate, a manual and a command stick! */
UPDATE dbscript_string SET content_loc3='%s beginnt zu tanzen.' WHERE entry=2000000108; /* %s begins to dance. */
UPDATE dbscript_string SET content_loc3='Haha! $n, Ihr seid mir schon ein Partner!' WHERE entry=2000000109; /* Hahah! $N, you make quite a partner! */
UPDATE dbscript_string SET content_loc3='%s beginnt, über den Herzen und Zungen einen Zauber zu sprechen.' WHERE entry=2000000110; /* %s begins to cast a spell over the hearts and tongues. */
UPDATE dbscript_string SET content_loc3='Die Brut von Onyxia!' WHERE entry=2000000111; /* The brood of Onyxia! */
UPDATE dbscript_string SET content_loc3='Okay, Leute. Macht euch an die Arbeit und bringt diese Knochen an.' WHERE entry=2000000112; /* Okay, crew. Get to work on getting these bones installed. */
UPDATE dbscript_string SET content_loc3='Ich brauche nur wenige Minuten, um das Serum zuzubereiten, $n.' WHERE entry=2000000113; /* The serum will only take a few minutes to prepare, $N. */
UPDATE dbscript_string SET content_loc3='Fast fertig...' WHERE entry=2000000114; /* Almost done... */
UPDATE dbscript_string SET content_loc3='Endlich ist das Serum vollendet, $n. Kommt jetzt näher. Wir müssen reden.' WHERE entry=2000000115; /* At last, the serum is complete, $N. Now, come closer. We must talk. */
UPDATE dbscript_string SET content_loc3='%s trinkt einen großen Schluck Bier.' WHERE entry=2000000116; /* %s takes big swig of ale. */
UPDATE dbscript_string SET content_loc3='Das war erfrischend. Jetzt zu den Informationen, die ich loswerden muss...' WHERE entry=2000000117; /* That was refreshing. Now there\'s information that needs to be told... */
UPDATE dbscript_string SET content_loc3='Ich denke, dass die Verlassenen die Verbündeten der Horde in die Irre führen... wartet... Ich fühle mich so... benommen...' WHERE entry=2000000118; /* I believe the Forsaken are misleading the allies of the Horde... wait... I feel so... dizzy... */
UPDATE dbscript_string SET content_loc3='%s windet sich vor Schmerzen.' WHERE entry=2000000119; /* %s writhes in pain. */
UPDATE dbscript_string SET content_loc3='AAAAAAAARGH!' WHERE entry=2000000120; /* AAAAAAAAAAAAAAAARGH */
UPDATE dbscript_string SET content_loc3='Hiermit zerstöre ich diese Instrumente des Bösen! Für die Horde!' WHERE entry=2000000121; /* I hereby destroy these instruments of evil! For the Horde! */
UPDATE dbscript_string SET content_loc3='Bitte folgt mir.' WHERE entry=2000000122; /* Please, follow me. */
UPDATE dbscript_string SET content_loc3='Ich habe noch nicht herausgefunden, was es genau ist, aber irgendetwas an den heißen Quellen ist seltsam...' WHERE entry=2000000123; /* I haven\'t really figured out what it is, but there\'s something strange about the hot springs... */
UPDATE dbscript_string SET content_loc3='%s wirft das Schwert in den seichten Teich.' WHERE entry=2000000124; /* %s tosses the sword into the shallow pool. */
UPDATE dbscript_string SET content_loc3='Ich habe etwas festgestellt. Immer, wenn ich etwas in dieses Wasser werfe, dann hat es sich geradezu unvorstellbar zu seinem Vorteil verändert, wenn ich es wieder heraushole.' WHERE entry=2000000125; /* I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it. */
UPDATE dbscript_string SET content_loc3='Na, was auch immer der Grund dafür sein mag, es funktioniert jedenfalls wunderbar!' WHERE entry=2000000126; /* Well, whatever it is, it works quite well! */
UPDATE dbscript_string SET content_loc3='Und ich bin fest entschlossen, mehr herauszufinden... Solltet Ihr also meine Hilfe wieder benötigen, dann wisst Ihr ja, wo Ihr mich finden könnt.' WHERE entry=2000000127; /* And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE dbscript_string SET content_loc3='Ehret Eure Helden! Heute haben sie einem unserer verhasstesten Feinde einen schweren Schlag beigebracht! Rend Schwarzfaust, der falsche Kriegshäuptling, ist gefallen!' WHERE entry=2000000128; /* Honor your heroes! On this day, they have dealt a great blow against one of our most hated enemies! The false Warchief, Rend Blackhand, has fallen! */
UPDATE dbscript_string SET content_loc3='Badet in meiner Macht! Trinkt meine Kraft! Kämpft zum Ruhm der Horde!' WHERE entry=2000000129; /* Be bathed in my power! Drink in my might! Battle for the glory of the Horde! */
UPDATE dbscript_string SET content_loc3='Diese Urkunden gehören mir, Bruder! Bald schon werdet Ihr mir nicht mehr im Wege stehen!' WHERE entry=2000000130; /* The deeds are mine, brother! Soon you shall be out of my way for good! */
UPDATE dbscript_string SET content_loc3='Oh Mann, ich dachte wirklich, dass das mein sicheres Ende gewesen sei. Ugh... bin noch ganz benommen...' WHERE entry=2000000131; /* Oh man, I thought I was dead for sure. Ugh... still dizzy... */
UPDATE dbscript_string SET content_loc3='Ich denke, ich komme alleine zur Festung zurück. Jetzt, da Ihr mir Zeit verschafft habt, dürfte ich mich herausschleichen können. Wer immer Ihr auch sein mögt...danke. Möge Elune Euch stets segnen!' WHERE entry=2000000132; /* I can get back to the Stronghold on my own, I think. Now that you bought me some time, I should be able to stealth out of here. Who ever you are... thank you. May Elune bless you always! */
UPDATE dbscript_string SET content_loc3='Das dürfte nicht lange dauern...' WHERE entry=2000000133; /* This shouldn\'t take long... */
UPDATE dbscript_string SET content_loc3='Der Unsichtbarkeitsschnaps steht für Euch bereit, $n.' WHERE entry=2000000134; /* The invisibility liquor is ready for you, $n */
UPDATE dbscript_string SET content_loc3='Das geht runter wie Nichts...' WHERE entry=2000000135; /* Here goes nothing... */
UPDATE dbscript_string SET content_loc3='Corbett, mein Teurer. Harlan benötigt so schnell wir können eine Ladung gestrickter Hemden und Hosen.' WHERE entry=2000000139; /* Corbett, dear. Harlan needs a load of knitted shirts and pants as soon as we can manage. */
UPDATE dbscript_string SET content_loc3='Corbett, seid Ihr da? Harlan benötigt noch eine Ladung Strickwaren. Könnt Ihr sie ihm bringen?' WHERE entry=2000000140; /* Corbett, you there? Harlan needs another load of knitted goods. Can you take it to him? */
UPDATE dbscript_string SET content_loc3='Es war mir ein Vergnügen, Zuckerschnute. Ich bin bald zurück...' WHERE entry=2000000141; /* My pleasure, sugar drop. I\'ll be back soon... */
UPDATE dbscript_string SET content_loc3='Die Geschäfte im Basar scheinen ja gut zu laufen. Ich werde ihm sofort Nachschub bringen!' WHERE entry=2000000142; /* Business must be good down at the bazaar. I\'ll get him resupplied right away! */
UPDATE dbscript_string SET content_loc3='Hm... wenn ich das hier losgeworden bin, werde ich vielleicht in dieses Käsegeschäft gehen und mir dort einen kleinen Imbiss genehmigen.' WHERE entry=2000000143; /* Hm... after dropping this off, I think I\'ll head to that cheese shop for a snack. */
UPDATE dbscript_string SET content_loc3='Ich müsste doch ein paar Münzen bei diesem Geschäft verdient haben. Vielleicht kaufe ich mir ein Mittagessen...' WHERE entry=2000000144; /* I should have a few extra coins from this sale. Maybe I\'ll buy myself some lunch... */
UPDATE dbscript_string SET content_loc3='He, Harlan. Hier ist eine Ladung Strickstoff für Euch.' WHERE entry=2000000145; /* Hey Harlan. Here\'s a load of knitted cloth for you. */
UPDATE dbscript_string SET content_loc3='Uff! Hier ist eine neue Ladung an Vorräten, Harlan. Euer Geschäft scheint ja gut zu laufen!' WHERE entry=2000000146; /* Oomph! Here\'s a load of supplies. It must be selling fast! */
UPDATE dbscript_string SET content_loc3='Ah ja, und pünktlich geliefert. Es ist wie immer ein Vergnügen, mit Euch Geschäfte zu machen, Corbett.' WHERE entry=2000000147; /* Ah yes, and promptly delivered. As always, it\'s a pleasure doing business with you, Corbett. */
UPDATE dbscript_string SET content_loc3='Ah, das weiß ich sehr zu schätzen, Corbett. Wir werden alles sofort in die Regale räumen.' WHERE entry=2000000148; /* Ahh, much appreciated, Corbett. We\'ll get these on the racks immediately. */
UPDATE dbscript_string SET content_loc3='Schön zu sehen, dass es Euch so gut geht, Harlan. Und ich hoffe, wir sehen uns bald wieder...' WHERE entry=2000000149; /* Glad to see you\'re doing so well, Harlan. And I hope to see you again soon? */
UPDATE dbscript_string SET content_loc3='Dann will ich mal wieder. Macht\'s gut, Harlan.' WHERE entry=2000000150; /* Well, I am off than. Take care, Harlan! */
UPDATE dbscript_string SET content_loc3='Nun zu diesem Imbiss...' WHERE entry=2000000151; /* content: Now for that snack... */
UPDATE dbscript_string SET content_loc3='Hallo, Triasklan! Eine Kugel von Eurem geräucherten Mozzarella, bitte!' WHERE entry=2000000152; /* Hullo, Trias clan! A ball of your smoked mozzarella, if you please! */
UPDATE dbscript_string SET content_loc3='Guten Tag, Elling! Hallo Elaine! Kann ich einen Laib Blaukäse bekommen?' WHERE entry=2000000153; /* Good day. Elling! Hullo Elaine! Let me have a wheel of blue cheese, eh? */
UPDATE dbscript_string SET content_loc3='Guten Tag, Corbett. Hier ist Euer Käse. Heute Morgen ganz frisch gemacht! Und wie läuft Euer Laden?' WHERE entry=2000000154; /* Good day, Corbett. Here\'s your cheese, fresh made this morning! And how are things at your shop? */
UPDATE dbscript_string SET content_loc3='Hallo, Corbett! Bitte sehr! Sehe ich es richtig, dass Euer Tuchladen gut läuft...?' WHERE entry=2000000155; /* Hi Corbett! Here, you go! I trust business is faring well at your clothier shop...? */
UPDATE dbscript_string SET content_loc3='Ja, gnädige Frau, das Geschäft läuft gut!' WHERE entry=2000000156; /* Yes ma\'am, business is brisk! */
UPDATE dbscript_string SET content_loc3='Danke für den Käse!' WHERE entry=2000000157; /* Thanks for the cheese! */
UPDATE dbscript_string SET content_loc3='Zeit, wieder zurück in den Laden zu gehen...' WHERE entry=2000000158; /* Time to get back to the shop... */
UPDATE dbscript_string SET content_loc3='Ich bin zurück!' WHERE entry=2000000159; /* I\'m back! */
UPDATE dbscript_string SET content_loc3='Ma reth bromo zoln kilagrin dra ma zoern tu ko fraht ka tadrom Ma krin drinor zaln dirnor Ma krin kan aasrugel korsul' WHERE entry=2000000160; /* Ma reth bromo zoln kilagrin dra ma zoern tu ko fraht ka tadrom Ma krin drinor zaln dirnor Ma krin kan aasrugel korsul */
UPDATE dbscript_string SET content_loc3='Aaarrrrr!!' WHERE entry=2000000161; /* ROARRRRR!! */
UPDATE dbscript_string SET content_loc3='DUNKELHAIN... ICH HUNGER!!' WHERE entry=2000000162; /* DARKSHIRE... I HUNGER!! */
UPDATE dbscript_string SET content_loc3='Wildherzen Euch töten, wenn Ihr zurück kommt. Ihr hier nicht mehr willkommen!' WHERE entry=2000000163; /* I said GOOD DAY! */
UPDATE dbscript_string SET content_loc3='Mein Schwert Archeus hat mir in meinem Leben gut gedient. Aber nun, da mein Geist mein glückloses Dasein verlässt, brauche ich es nicht länger.' WHERE entry=2000000164; /* My sword Archeus served me well in life, but as at last my spirit may pass from the unhappy existence, I need it no longer. */
UPDATE dbscript_string SET content_loc3='Ich werde mich an die Liebe meiner Tochter klammern und hoffen, dass mir im Licht meine Sünden vergeben werden.' WHERE entry=2000000165; /* I shall cling to the love of my daughter and hope that I will find forgiveness under the Light for my sins. */
UPDATE dbscript_string SET content_loc3='OH NEIN! Der König is tot! Eh... lang lebe der neue König! Jo!' WHERE entry=2000000166; /* OH NOES! Da king is dead! Uh... hail to da new king! Yeah! */
UPDATE dbscript_string SET content_loc3='Yar, er ist jetzt tot. Das macht Euch den neue König... äh, Euch alle! Gordok gehören jetzt Euch, Boss! Wenn Ihr lernen wollt, was König alles machen kann, fragen mich. Ich war... Berater! Jaha, darum mich alle nennen der Gewiefte!' WHERE entry=2000000167; /* Yar, he\'s dead all right. That makes you da new king... well, all of you! Gordok is yours now, boss! You should talk to me so you can learn everything there is about being da king! I was... is his assistant! Yeah, that\'s why I\'m called da crafty one! */
UPDATE dbscript_string SET content_loc3='%s starrt nach Nordwesten und lässt ein langes, herausgezogenes Heulen von sich.' WHERE entry=2000000168; /* %s stares to the northwest and lets out a long, drawn out howl. */
UPDATE dbscript_string SET content_loc3='%s lässt ein mächtiges Heulen von sich.' WHERE entry=2000000169; /* %s lets forth a mighty howl. */
UPDATE dbscript_string SET content_loc3='Wie Ihr befehlt!' WHERE entry=2000000170; /* By your command */
UPDATE dbscript_string SET content_loc3='Wartet hier, $n. Der Spionagebot wird Lescovar schnellstens herauslocken. Seid bereit! Greift erst an, wenn Ihr das Gespräch mitgehört habt.' WHERE entry=2000000171; /* Wait here, $N. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation. */
UPDATE dbscript_string SET content_loc3='Einen guten Tag Euch beiden. Ich möchte Lord Lescovar sprechen.' WHERE entry=2000000172; /* Good day to you both. I would speak to Lord Lescovar */
UPDATE dbscript_string SET content_loc3='Natürlich. Er erwartet Euch in der Bibliothek.' WHERE entry=2000000173; /* Of course. He awaits you in the library */
UPDATE dbscript_string SET content_loc3='Danke. Das Licht sei mit Euch beiden.' WHERE entry=2000000174; /* Thank you. The Light be with you both. */
UPDATE dbscript_string SET content_loc3='Mylord, Euer Gast ist da. Er erwartet Euch.' WHERE entry=2000000175; /* Milord, your guest has arrived. He awaits your presence. */
UPDATE dbscript_string SET content_loc3='Ah, vielen Dank. Bleibt hier in der Bibliothek, während ich mich um diese kleine Angelegenheit kümmere.' WHERE entry=2000000176; /* Ah, thank you kindly. I will leave you to the library while I tend to this small matter. */
UPDATE dbscript_string SET content_loc3='Ich werde die Zeit zu nutzen wissen, Mylord. Danke.' WHERE entry=2000000177; /* I shall use this time wisely, milord. Thank you. */
UPDATE dbscript_string SET content_loc3='Es ist Zeit für meine Meditation, lasst mich allein.' WHERE entry=2000000178; /* It\'s time for my meditation, leave me. */
UPDATE dbscript_string SET content_loc3='Ja, Sire!' WHERE entry=2000000179; /* Yes, sir! */
UPDATE dbscript_string SET content_loc3='%s wartet, bis die Wachen außer Sichtweite sind.' WHERE entry=2000000180; /* %s waits for the guards to be out of sight. */
UPDATE dbscript_string SET content_loc3='Da seid Ihr ja. Was gibt es für Neuigkeiten aus Westfall?' WHERE entry=2000000181; /* There you are. What news from Westfall? */
UPDATE dbscript_string SET content_loc3='Van Cleef lässt mitteilen, dass die Pläne unterwegs seien. Doch er hat Gerüchte vernommen, dass irgendjemand herumschnüffelt.' WHERE entry=2000000182; /* VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about. */
UPDATE dbscript_string SET content_loc3='Hmm, das könnte dieser Schnüffler Shaw sein. Ich werde sehen, was ich herausfinden kann. Und nun fort mit Euch. Ich werde Euch bald wieder aufsuchen.' WHERE entry=2000000183; /* Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon. */
UPDATE dbscript_string SET content_loc3='Wohlan denn. Dann werde ich zurückkehren.' WHERE entry=2000000184; /* Very well. I will return then. */
UPDATE dbscript_string SET content_loc3='Das ist es! Darauf habt Ihr gewartet! TÖTET SIE!' WHERE entry=2000000185; /* That\'s it! That\'s what you were waiting for! KILL THEM! */
UPDATE dbscript_string SET content_loc3='Verteidigt euch, Dummkopf!' WHERE entry=2000000186; /* Defend yourself, fool! */
UPDATE dbscript_string SET content_loc3='Die Monstrosität ist in das Lager der Nachtwache eingefallen! Schnell, wir müssen sie abfangen, bevor sie die Stadt erreicht!' WHERE entry=2000000187; /* The abomination has overrun the Night Watch camp! Quickly, we must intercept it before it reaches town! */
UPDATE dbscript_string SET content_loc3='Die Bestie wurde erschlagen! Jetzt herrscht wieder Ruhe in Dunkelhain!' WHERE entry=2000000188; /* The beast is slain! All is well in Darkshire. */
UPDATE dbscript_string SET content_loc3='Stacheleberfetzen! Die dürften den Zweck erfüllen. Fische lieben Stacheleber.' WHERE entry=2000000189; /* Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE dbscript_string SET content_loc3='Hab einen gefangen! Werde mal sehen, ob eine der Wachen hungrig ist. Komm mit Reißzahn!' WHERE entry=2000000190; /* Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE dbscript_string SET content_loc3='Falls Ihr hungrig sein solltet, ich habe einen Fisch gefangen...' WHERE entry=2000000191; /* I caught a fish if you are hungry... */
UPDATE dbscript_string SET content_loc3='Das nenne ich einen guten Fang! Ihr ehrt die Horde, Junge, wir danken dir!' WHERE entry=2000000192; /* A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE dbscript_string SET content_loc3='Wie Ihr wünscht...' WHERE entry=2000000195; /* As you wish... */
UPDATE dbscript_string SET content_loc3='Ich bezweifle stark, dass er irgendwas dazu zu sagen hat, Fremder...' WHERE entry=2000000196; /* I very much doubt that he will have anything to say, stranger... */
UPDATE dbscript_string SET content_loc3='Ich persönlich hatte kein Problem mit ihm. Es war der Prinz, der an ihm als Hochelf ein Exempel statuieren ließ. Leider bin ich nicht derjenige, der den Prinzen in Frage stellen kann. Zur seiner Verteidigung muss ich noch anmerken, dass er Meister Winthalus nicht sofort einäschern ließ.' WHERE entry=2000000197; /* 'I personally didn\'t mind him. It was the Prince who took exception to a high elf in his domain. Alas, I am not one to question the Prince. In his defense, he did not incinerate Master Winthalus immediately. */
UPDATE dbscript_string SET content_loc3='Wir möchten nicht, dass noch irgendjemand auf uns aufmerksam wird, Fremder. Kehrt zu denen zurück, die Euch hierher geschickt haben, und sagt ihnen, dass Ihr nichts finden konntet. Wenn Ihr das getan habt (und ich werde erfahren, ob Ihr meinen Rat befolgt habt), kehrt zu mir zurück, und ich werde Euch über das Schicksal der Verstorbenen aufklären.' WHERE entry=2000000198; /* We most definitely do not need any more attention drawn to us, stranger. Return to those that sent you in search of the lost master and tell them that nothing could be found. When this is done - and I will know when it is so - return and I shall reveal the secrets of the deceased. */
UPDATE dbscript_string SET content_loc3='Hmmm... wo ist mein Angelhaken? Oh, da ist er ja.' WHERE entry=2000000199; /* Hmmm.... Where is my fishing hook? Oh, there it is. */
UPDATE dbscript_string SET content_loc3='Mal sehen, ob die Fische anbeißen.' WHERE entry=2000000200; /* Let\'s see if the fish are biting. */
UPDATE dbscript_string SET content_loc3='Hmmm... ich glaube ich brauche einen besseren Köder. Ich werde mal im Wagen meines Vaters nachsehen.' WHERE entry=2000000201; /* Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE dbscript_string SET content_loc3='Was? $n, Ihr habt mir gut gedient, aber...' WHERE entry=2000000234; /* What? $N, you served me well, but... */
UPDATE dbscript_string SET content_loc3='Warum habt Ihr mich so früh beschworen? Ich habe meine volle Macht noch nicht erreicht!' WHERE entry=2000000235; /* Why have you summoned me so soon? I haven\'t yet reached my full power! */
UPDATE dbscript_string SET content_loc3='Einerlei. Ihr wart dumm genug, mir zu helfen, und jetzt werdet Ihr dafür bezahlen!' WHERE entry=2000000236; /* No matter. You were a fool to help me, and now you will pay! */
UPDATE dbscript_string SET content_loc3='Nochmals danke, $n. Ich werde jetzt einfach hier warten, bis ich ungefährdet gehen kann.' WHERE entry=2000000237; /* Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE dbscript_string SET content_loc3='Die Anzahl meiner Gefährten schwindet, Göttin, und meine eigene Macht wird bald nicht mehr ausreichen, die Dämonen des Teufelswaldes zurückzuhalten.' WHERE entry=2000000238; /* The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood. */
UPDATE dbscript_string SET content_loc3='Göttin, gewähre mir die Stärke, meine Feinde zu besiegen! Hör mich bitte an, ich bin verzweifelt in meiner Not und meine Sache ist gerecht!' WHERE entry=2000000239; /* Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just! */
UPDATE dbscript_string SET content_loc3='Was... was ist das? Könnte das die Antwort auf meine Gebete sein? Elune hat mir eine Waffe gewährt - diese Sense -, um die Dämonen zu besiegen.' WHERE entry=2000000240; /* What... what is this? Could this by the answer to my prayers? Elune has granted me a weapon -- this scythe -- to defeat the demons. */
UPDATE dbscript_string SET content_loc3='Ich habe versagt... Ich habe in meiner Pflicht versagt... habe mein Volk enttäuscht... Die Worgs sind wild und die Sense meiner Göttin ist verloren.' WHERE entry=2000000241; /* I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost. */
UPDATE dbscript_string SET content_loc3='Das Böse, das ich entfesselt habe... In wessen Hand mag die Stärke, die meine Göttin mir gewährte, nun liegen?... Wessen Hand führt sie nun?' WHERE entry=2000000242; /* This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed? */
UPDATE dbscript_string SET content_loc3='Terenthis, Raene schickte mich, um Euch zu suchen. Die Schildwachen des Eschentals brauchen Hilfe.' WHERE entry=2000000260; /* Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale. */
UPDATE dbscript_string SET content_loc3='He, Nagmara! Wie wäre es mit einem Becher Dunkeleisenbier?' WHERE entry=2000000261; /* Hey Nagmara! How about a mug of Dark Iron Ale? */
UPDATE dbscript_string SET content_loc3='Nagmara! Ist noch Hammelfleisch übrig?' WHERE entry=2000000262; /* Nagmara! Have any mutton left? */
UPDATE dbscript_string SET content_loc3='Herrin, sagt Stöpsel, dass ich noch mehr von seinem Bier möchte. Ich bekomme nicht genug davon!' WHERE entry=2000000263; /* Mistress, you tell Plugger that I want more of his ale. I can\'t get enough! */
UPDATE dbscript_string SET content_loc3='He, Nag, würdet Ihr mir davon wohl eine Scheibe abschneiden...' WHERE entry=2000000264; /* Hey Nag, how about you slice me off a piece of that... */
UPDATE dbscript_string SET content_loc3='Alles klar!' WHERE entry=2000000265; /* You got it! */
UPDATE dbscript_string SET content_loc3='Kommt sofort!' WHERE entry=2000000266; /* Coming right up! */
UPDATE dbscript_string SET content_loc3='Ich bringe es sofort, Baby...' WHERE entry=2000000267; /* I\'ll bring it right over, baby... */
UPDATE dbscript_string SET content_loc3='Die Macht der Träne der Monde gehört jetzt mir! Mir ganz allein!' WHERE entry=2000000345; /* The power of the Tear of the Moons is mine! Mine I say! */
UPDATE dbscript_string SET content_loc3='%s beginnt, die Träne der Monde zu reiben.' WHERE entry=2000000346; /* %s begins to rub the Tear of the Moons. */
UPDATE dbscript_string SET content_loc3='Macht! Glorreiche Macht!' WHERE entry=2000000347; /* Power! Glorious power! */
UPDATE dbscript_string SET content_loc3='%s beginnt, seltsame Grunzgeräusche von sich zu geben. Die Träne der Monde fällt zu Boden und zerspringt.' WHERE entry=2000000348; /* %s begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters. */
UPDATE dbscript_string SET content_loc3='Spendet den Wohltätigkeitsorganisationen, die den Opfern dieser schweren Zeit zu helfen versuchen! Bitte.' WHERE entry=2000000349; /* Give to the charities who seek to help the victims of these hard times! Please. */
UPDATE dbscript_string SET content_loc3='Vergesst nicht die Waisen aus Sturmwind!' WHERE entry=2000000350; /* Don\'t forget the orphans of Stormwind! */
UPDATE dbscript_string SET content_loc3='Gebt von Herzen und aus vollen Taschen! Gebt für die Kinder aus Sturmwind, die ihre Eltern verloren haben.' WHERE entry=2000000351; /* Give of your hearts and your purses! Give to the children of Stormwind who have lost their parents. */
UPDATE dbscript_string SET content_loc3='Helft den Kindern aus Sturmwind... den Opfern des Krieges und der Seuche!' WHERE entry=2000000352; /* Help the children of Stormwind... victims of the war and plague! */
UPDATE dbscript_string SET content_loc3='Ihr werdet hier sterben. \<Visionen von Chaos und Zerstörung strömen in Eure Gedanken.\>' WHERE entry=2000000353; /*  You will perish here. <Your mind fills with visions of chaos and destruction.> */
UPDATE dbscript_string SET content_loc3='Verlasst diesen Ort. <Ein weiß glühender Flammenbogen geht von dem Relikt aus. Eine Erinnerung wurde erlangt: Ein einsamer Dunkeleisenzwerg kniet, von sieben Toten umgeben, vor einem Flammenmonolithen.>' WHERE entry=2000000354; /* Leave this place. <The relic emits a white hot arc of flame. A memory has been gained: A lone Dark Iron dwarf is surrounded by seven corpses, kneeling before a monolith of flame.> */
UPDATE dbscript_string SET content_loc3='Entweiher... für diesen Frevel werdet Ihr bestraft. <Das Symbol einer Flamme geht von dem Relikt aus, bevor es zerfällt.>' WHERE entry=2000000355; /* Defiler... you will be punished for this incursion. <A symbol of flame radiates from the relic before it crumbles to the earth.> */
UPDATE dbscript_string SET content_loc3='Helft uns, Fremdling. <Das Relikt zerfällt zu Staub. Eine Vision von acht Dunkeleisenzwergen, die eine Art Ritual durchführen, erstrahlt vor Eurem geistigen Auge.>' WHERE entry=2000000356; /* Help us, outsider. <The relic crumbles to dust. A vision of eight Dark Iron dwarves performing some sort of ritual fills your head.> */
UPDATE dbscript_string SET content_loc3='Eure Existenz wird zur Kenntnis genommen. <Das Relikt zerfällt zu Staub. Neu erlangtes Wissen pulsiert in Eurem Kopf. Etwas Böses lauert im Herzen des Berges.>' WHERE entry=2000000357; /* Your existence is acknowledged. <The relic turns to dust. Your head throbs with newfound wisdom. Something evil lurks in the heart of the mountain.> */
UPDATE dbscript_string SET content_loc3='Verlasst diesen Ort. <Ein weiß glühender Flammenbogen geht von dem Relikt aus. Eine Erinnerung wurde erlangt: Ein einsamer Dunkeleisenzwerg kniet, von sieben Toten umgeben, vor einem Flammenmonolithen.>' WHERE entry=2000000358; /* Leave this place. <The relic emits a white hot arc of flame. A memory has been gained: A lone Dark Iron dwarf is surrounded by seven corpses, kneeling before a monolith of flame.> */
UPDATE dbscript_string SET content_loc3='Er kann nicht besiegt werden. <Das Relikt brennt nieder, bis nichts mehr übrig ist. Die Erinnerungen, die es enthielt, sind jetzt Eure eigenen. Diese Stadt wurde von einem Wesen zerstört, das nicht von dieser Welt ist.>' WHERE entry=2000000359; /* He cannot be defeated. <The relic burns to nothing. The memories it held are now your own. This city was destroyed by a being not of this world.> */
UPDATE dbscript_string SET content_loc3='Besudelt diese Ruinen nicht, sterbliches Wesen. <Ihr werdet von einem grellen Lichtblitz eingehüllt. Eine Kreatur, die ganz aus Feuer besteht, ist der einzige Anblick, an den Ihr Euch erinnern könnt.>' WHERE entry=2000000360; /* Do not taint these ruins, mortal. <You are engulfed in a blinding flash of light. A creature composed entirely of flame is the only thing you can remember seeing.> */
UPDATE dbscript_string SET content_loc3='Kommt, $n. Der See befindet sich direkt hinter der nächsten Biegung.' WHERE entry=2000000375; /* Come,  $N. The lake is around the bend. */
UPDATE dbscript_string SET content_loc3='Bleibt in meiner Nähe, $n. Ich werde Euch beschützen, falls etwas schief geht.' WHERE entry=2000000376; /* Stand near me,  $N. I will protect you should anything go wrong. */
UPDATE dbscript_string SET content_loc3='Malfurion!' WHERE entry=2000000377; /* Malfurion! */
UPDATE dbscript_string SET content_loc3='Remulos, mein alter Freund. Es ist schön, Euch wiederzusehen. Ich wusste, dass meine Botschaft Euch erreichen würde, auf dem einen oder auf dem anderen Weg.' WHERE entry=2000000378; /* Remulos,  old friend. It is good to see you once more. I knew the message would find its way to you - one way or another. */
UPDATE dbscript_string SET content_loc3='Alles ist von schrecklichen Alpträumen durchzogen, Malfurion. Was geht im Smaragdgrünen Traum nur vor? Was könnte ihn derartig grausam verzerren?' WHERE entry=2000000379; /* It was shrouded in nightmares,  Malfurion. What is happening in the Dream? What could cause such atrocities? */
UPDATE dbscript_string SET content_loc3='Ich fürchte das Schlimmste, alter Freund. Innerhalb des Smaragdgrünen Traums bekämpfen wir einen neuen Feind, geboren aus uraltem Bösen. Yseras edle Brut ist den uralten Einflüsterungen erlegen. Es scheint, als sei es dem Alptraum gelungen die Grenzen der Traumwelt zu durchbrechen, um sich in Azeroth einen neuen Wirt zu suchen.' WHERE entry=2000000380; /* I fear for the worst,  old friend. Within the Dream we fight a new foe,  born of an ancient evil. Ysera\'s noble brood has fallen victim to the old whisperings. It seems as if the Nightmare has broken through the realm to find a new host on Azeroth. */
UPDATE dbscript_string SET content_loc3='Ich habe es auch gespürt, Malfurion. Dunkle Zeiten werfen ihre Schatten voraus.' WHERE entry=2000000381; /* I sensed as much,  Malfurion. Dark days loom ahead. */
UPDATE dbscript_string SET content_loc3='Ja, Remulos. Die sterblichen Völker müssen auf das Kommende vorbereitet werden.' WHERE entry=2000000382; /* Aye Remulos,  prepare the mortal races. */
UPDATE dbscript_string SET content_loc3='Ihr seid schon zu lange fort, Malfurion. Der Frieden zwischen den Kindern Azeroths ist bestenfalls brüchig. Was ist mit meinem Vater? Eurem Bruder? Habt Ihr Neuigkeiten?' WHERE entry=2000000383; /* You have been gone too long,  Malfurion. Peace between the Children of Azeroth has become tenuous at best. What of my father? Of your brother? Have you any news? */
UPDATE dbscript_string SET content_loc3='Cenarius kämpft an meiner Seite. Illidan verweilt, vor sich hinbrütend, auf seinem Thron in der Scherbenwelt. Ich befürchte, dass die Niederlage gegen Arthas ihn innerlich gebrochen hat. Er scheint dem Wahnsinn anheim gefallen zu sein, Remulos. In seinen Gedanken wiederholt er immer und immer wieder die vergangenen Ereignisse, doch in Gedanken macht er alles richtig. In seinem Verstand besiegt er Arthas ein auf das andere Mal, es ist ein endloser Kreislauf. Er ist schon zu weit von uns gegangen, mein alter Freund. Ich fürchte, es naht die Zeit, in der unser Freundschaft auf die Probe gestellt wird, ganz anders als damals am Brunnen in Zin-Azshari.' WHERE entry=2000000384; /* Cenarius fights at my side. Illidan sits atop his throne in Outland - brooding. I\'m afraid that the loss to Arthas proved to be his breaking point. Madness has embraced him,  Remulos. He replays the events in his mind a thousand times per day,  but in his mind,  he is the victor and Arthas is utterly defeated. He is too far gone,  old friend. I fear that the time may soon come that our bond is tested and it will not be as it was at the Well in Zin-Azshari. */
UPDATE dbscript_string SET content_loc3='Remulos, ich werde zurückgezogen... Tyrande... sende ihr meine Liebe... sagt ihr, dass es mir gut geht. Sagt ihr, dass ich zurückkehren werde... Lebt wohl...' WHERE entry=2000000385; /* Remulos,  I am being drawn back... Tyrande... send her my love... Tell her I am safe... Tell her... Tell her I will return... Farewell... */
UPDATE dbscript_string SET content_loc3='Lebt wohl, alter Freund... Lebt wohl...' WHERE entry=2000000386; /* Farewell,  old friend... Farewell... */
UPDATE dbscript_string SET content_loc3='Lasst uns zum Hain zurückkehren, Sterblicher.' WHERE entry=2000000387; /* Let us return to the grove,  mortal. */
UPDATE dbscript_string SET content_loc3='Hallo, Selarin. Ich kann Euch momentan leider nicht helfen, aber vielleicht können es einige der Abenteurer hier in Auberdine...' WHERE entry=2000000395; /* Hello, Selarin. I\'m afraid I cannot help you at the moment, but perhaps some of the adventurers here in Auberdine can... */
UPDATE dbscript_string SET content_loc3='Habt Dank, Terenthis. Ich werde hier bleiben, solange ich kann.' WHERE entry=2000000396; /* Thank you, Terenthis. I shall remain here for as long as I can. */
UPDATE dbscript_string SET content_loc3='Hoffentlich kann ich eine würdige Unterstützung schicken, um dem Eschental und den Schildwachen zu helfen.' WHERE entry=2000000397; /* Hopefully I can send a worthy few to help Ashenvale and the Sentinels. */
UPDATE dbscript_string SET content_loc3='Es tut mir ja schrecklich leid, einen Kunden einfach stehen zu lassen, aber wir müssen gerade mal woanders ein paar Leute übers Ohr hauen. Wir sind aber später wieder da. Lebt wohl!' WHERE entry=2000000400; /* 'So sorry to leave a customer but we have places to go and people to swindle. We will be back sometime later today. Good-bye!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Cork Gizelton Leaving Kormek\'s Hut */
UPDATE dbscript_string SET content_loc3='Ich suche nach Leibwächtern, die Gizeltons Karawane beschützen möchten. Wir halten auf der Straße östlich von Kormeks Hütte, nördlich vom Dorf der Kolkarzentauren.' WHERE entry=2000000401; /* I am looking for some bodyguards that would like to protect the Gizelton Caravan. We are stopped on the road east of Kormek\'s Hut, north of Kolkar Centaur Village. */
UPDATE dbscript_string SET content_loc3='Es ist Zeit, dass Gizeltons Karawane aufbricht! Wir kommen bald wieder, aber wenn Ihr gar nicht warten könnt, dann kommt nach Norden zu Kormeks Hütte. Dort öffnen wir so etwa in einer Stunde.' WHERE entry=2000000402; /* Time for the Gizelton Caravan to head on out! We\'ll be back soon but if you cannot wait, head north to Kormek\'s Hut. We open shop in about an hour. */
UPDATE dbscript_string SET content_loc3='Hier spricht Rigger Gizelton - ich brauche für meine Karawane eine Eskorte, um am Zirkel der Mannoroc vorbeizukommen. Ich bin auf der Straße östlich von Schattenflucht.' WHERE entry=2000000403; /* This is Rigger Gizelton asking for assistance escorting my caravan past Mannoroc Coven. I\'m on the road east of Shadowprey village. */
UPDATE dbscript_string SET content_loc3='Seid bereit! Wenn die Naga erst wissen, dass wir am Leben sind, werden sie nicht zögern, ihren Vorteil zu nutzen.' WHERE entry=2000000466; /* Stand Prepared! The naga won\'t wait long to press their advantage once they know we\'re alive. */
UPDATE dbscript_string SET content_loc3='Ihr Kinder des Grabes, hört meinen Ruf!' WHERE entry=2000000500; /* Children of the Night, heed your Queen\'s call! */
UPDATE dbscript_string SET content_loc3='Ich beglückwünsche Sie zu dieser am meisten verehrten Nacht - die Nacht, in der wir Verlassene das Joch der Unterdrückung der Geißel gebrochen haben!' WHERE entry=2000000501; /* I join you in celebration of this most revered of nights - the night we Forsaken broke the Scourge\'s yoke of oppression! */
UPDATE dbscript_string SET content_loc3='In dieser Nacht fürchten uns unsere Feinde am meisten. In dieser Nacht zeigen wir unseren Feinden, was es heißt, gegen die Verlassenen zu stehen!' WHERE entry=2000000502; /* It is this night that our enemies fear us the most. It is THIS night that we show our enemies what it means to stand against the Forsaken! */
UPDATE dbscript_string SET content_loc3='Wir verbrennen das Abbild des Weidenmanns als Symbol für unseren Kampf gegen diejenigen, die uns widersetzen. Wir tragen die Asche des verbrannten Weidenmanns als ein Symbol unseres nie endenden Kampfes gegen diejenigen, die uns versklaven würden.' WHERE entry=2000000503; /* We burn the effigy of the Wickerman as a symbol of our struggle against those who would oppose us. We wear the ashes of the burnt Wickerman as a symbol of our neverending fight against those who would enslave us. */
UPDATE dbscript_string SET content_loc3='Jetzt ist es an der Zeit, die Welt zu erschüttern! JETZT ist die Zeit, diejenigen, die uns versklaven wollen, daran zu erinnern, dass wir niemals nachgeben werden' WHERE entry=2000000504; /* Now is the time to shake the world to its foundations! NOW is the time to remind those who would enslave us that we shall never yield! */
UPDATE dbscript_string SET content_loc3='JETZT ist die Zeit der Verlassenen! Alle Macht den Verlassenen - JETZT UND FÜR IMMER !!!' WHERE entry=2000000505; /* NOW is the time of the Forsaken! Power to the Forsaken - NOW AND FOREVER!!! */
UPDATE dbscript_string SET content_loc3='Die Verbrennung des Weidenmanns beginnt in dreizig Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!' WHERE entry=2000000508; /* The burning of the Wickerman will begin in thirty minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge! */
UPDATE dbscript_string SET content_loc3='Die Verbrennung des Weidenmanns beginnt in fünfzehn Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!' WHERE entry=2000000509; /* The burning of the Wickerman will begin in fifteen minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge! */
UPDATE dbscript_string SET content_loc3='Die Verbrennung des Weidenmanns beginnt in fünf Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!' WHERE entry=2000000510; /* The burning of the Wickerman will begin in five minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge! */
UPDATE dbscript_string SET content_loc3='Das Fest des Weidenmanns beginnt jetzt! Alle Macht den Verlassenen!' WHERE entry=2000000511; /* The Wickerman Festival now begins! Power to the Forsaken! */
UPDATE dbscript_string SET content_loc3='Ich muss meine Energien auf dieses Messer konzentrieren. Dadurch sollte ich einige der stärkeren übersinnlichen Eindrücke wahrnehmen können, die es besitzt.' WHERE entry=2000000521; /* I will need to focus my energies onto this knife. By doing so, I should catch some of the stronger psychic impressions associated with it. */
UPDATE dbscript_string SET content_loc3='Ich... Ich habe genug gesehen, glaube ich... das ist überaus anstrengend...' WHERE entry=2000000522; /* I... I\'ve seen enough I think... so very draining... */
UPDATE dbscript_string SET content_loc3='Feralas: Geschichte... Hmmmm... Da will ich mal einen Blick hineinwerfen...' WHERE entry=2000000523; /* A History... Hmmmm... Let me examine this for a moment. */
UPDATE dbscript_string SET content_loc3='%s überfliegt die Seiten des Buchs.' WHERE entry=2000000524; /* %s skims the pages of the book. */
UPDATE dbscript_string SET content_loc3='Erstaunlich! Ich glaube, ich habe es, $n!' WHERE entry=2000000525; /* Amazing! I think I have found it, $N. */
UPDATE dbscript_string SET content_loc3='Glorreich, in der Tat, Malzdarr. Es scheint, mein Plan ist aufgegangen. Würdet Ihr mir nicht zustimmen, sterbliches Wesen?' WHERE entry=2000000526; /* Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal? */
UPDATE dbscript_string SET content_loc3='%s lacht ein reptilienhaftes Lachen... zumindest haltet Ihr es für ein Lachen.' WHERE entry=2000000527; /* %s lets loose a reptilian laugh... at least you think it\'s a laugh. */
UPDATE dbscript_string SET content_loc3='Ihr hättet der Schwarzfelslegion sicher nicht geholfen, wenn wir Euch nur um Unterstützung gebeten hätten, oder?' WHERE entry=2000000528; /* You would not have helped the legion of Blackrock had we just asked for your assistance, now would you? */
UPDATE dbscript_string SET content_loc3='Was sollen wir mit ihnen tun, Meister? Sie sind uns nicht länger von Nutzen.' WHERE entry=2000000529; /* What will we do with them, master? They are of no use to us anymore. */
UPDATE dbscript_string SET content_loc3='RUHE, SCHWACHKOPF!' WHERE entry=2000000530; /* SILENCE FOOL! */
UPDATE dbscript_string SET content_loc3='Sie sollen leben und sehen, was sie angerichtet haben: Sie sollen die Zerstörung und das Chaos sehen, das die Legion über die Schlucht bringen wird.' WHERE entry=2000000531; /* They will live of only to see the fruits of their labor: the destruction and chaos that will surely ensue as the legion of Blackrock invade the gorge. */
UPDATE dbscript_string SET content_loc3='Ich verlasse Euch jetzt, sterbliche Wesen. Lebend und mit diesem Plunder. Malzdarr! Gebt ihnen den Plunder.' WHERE entry=2000000532; /* I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets! */
UPDATE dbscript_string SET content_loc3='Sofort, Boss!' WHERE entry=2000000533; /* Right away, master! */
UPDATE dbscript_string SET content_loc3='%s schlägt schneller mit seinen riesigen Schwingen. Gleich wird er abheben.' WHERE entry=2000000534; /* %s begins to flap his massive wings faster. He is preparing for flight. */
UPDATE dbscript_string SET content_loc3='Oh ja, die abgestreifte Drachenhaut: verliert sie nicht. In der Brennenden Steppe gibt es welche meiner Art, die Dinge fertigen, die nur die Abgestreifte Haut des schwarzen Drachenschwarms festigen kann.' WHERE entry=2000000535; /* Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify. */
UPDATE dbscript_string SET content_loc3='Die Schwarzfelslegion naht, ihr Winzlinge! Die Erde wird brennen und der Himmel in Flammen stehen. Niemand wird überleben!' WHERE entry=2000000536; /* The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive... */
UPDATE dbscript_string SET content_loc3='Mal sehen... Wenn ich Recht habe, dann... Hmmm...' WHERE entry=2000000540; /* Now, let\'s see... If I am correct, then... Hmmm... */
UPDATE dbscript_string SET content_loc3='Blau plus rot... Ich würde mal sagen...' WHERE entry=2000000541; /* Blue plus red... I predict... */
UPDATE dbscript_string SET content_loc3='$n, das ist es! Endlich weiß ich Bescheid!' WHERE entry=2000000542; /* $N, that\'s it! I have finally figured it out! */
UPDATE dbscript_string SET content_loc3='Boah! So hatten wir nicht gewettet. Nee, nee, jetzt seid Ihr auf Euch allein gestellt, Slim!' WHERE entry=2000000602; /* Whoa! This is way more than what I bargained for, you\'re on your own, Slim! */
UPDATE dbscript_string SET content_loc3='%s löst ihre Umklammerung des Tagebuchs.' WHERE entry=2000000612; /* %s loosens her grasp on the journal she had been clutching. */
UPDATE dbscript_string SET content_loc3='Auf die Plätze. Fertig. LOS!' WHERE entry=2000000613; /* Ready. Set. GO */
UPDATE dbscript_string SET content_loc3='Hey, das war aber nicht besonders sportlich!' WHERE entry=2000000614; /* HEY! No fair! */
UPDATE dbscript_string SET content_loc3='Ich hab gewonnen!' WHERE entry=2000000615; /* I win! */
UPDATE dbscript_string SET content_loc3='Du hast ja geschummelt!' WHERE entry=2000000616; /* You cheated! */
UPDATE dbscript_string SET content_loc3='Na, noch ein Rennen? Zwei von drei gewinnt!' WHERE entry=2000000617; /* Wanna race again? Best two out of three! */
UPDATE dbscript_string SET content_loc3='Kommandant, Ihre Nachricht hat uns erreicht, jedoch ist unser Eingreifen hier in keinster Weise vom Obersten Kreuzfahrer genehmigt worden.' WHERE entry=2000000618; /* Your message did reach us, Commander, but by no means is our involvement here sanctioned by the Grand Crusader. */
UPDATE dbscript_string SET content_loc3='Euer Anführer lässt sich vor den Toren Naxxramas\' nieder. Denkt Ihr nicht, dass er die gleiche Anspannung empfindet, wie alle anderen auch?' WHERE entry=2000000619; /* Your leader takes residence at the doorstep to Naxxramas. Do you think he does not feel the pressure? */
UPDATE dbscript_string SET content_loc3='%s senkt seine Stimme zu einem Flüstern.' WHERE entry=2000000620; /* %s lowers the sound of his voice to a whisper. */
UPDATE dbscript_string SET content_loc3='Selbst Dämonen vermögen sich zu fürchten...' WHERE entry=2000000621; /* Even demons are capable of fear... */
UPDATE dbscript_string SET content_loc3='Die Aktivitäten der Geißel in den Pestländern haben sich verzehnfacht. Selbst aus weit entfernten Ländern wie Kalimdor erhalten wir Berichte über massive Angriffe der Geißel. Nun, wie glaubt Ihr also, konnte das passieren? Wie haben die verfluchten Untoten es bloß geschafft, das bisher Undenkbare zu schaffen? Ist es überhaupt noch irgendwo sicher?' WHERE entry=2000000622; /* Scourge activity in the Plaguelands has increased ten-fold. We are receiving reports of Scourge attacks from as far away as Kalimdor! Now how do you suppose this has happened? How have the wretched undead done that which was previously thought impossible? Is nowhere safe? */
UPDATE dbscript_string SET content_loc3='Die Ungläubigen werden handeln müssen. Orcs, Stiermenschen...' WHERE entry=2000000623; /* The heathens will be forced to react. Orcs, bull-men... */
UPDATE dbscript_string SET content_loc3='Tauren.' WHERE entry=2000000624; /* Tauren. */
UPDATE dbscript_string SET content_loc3='Wie auch immer. Dieser aufrührerische, verwahrloste Hordenabschaum...' WHERE entry=2000000625; /* Whatever. The revolting, unkempt Horde filth... */
UPDATE dbscript_string SET content_loc3='Euch fehlt es an Taktgefühl, Kommandant, was mich bei Eurer Person allerdings nicht weiter überrascht. Wie auch immer, Ihr habt Recht mit Eurer Meinung, dass die Horde handeln muss. Diesen Kampf wird die Horde nicht alleine schlagen. Helden aus aller Welt sind bereits eingetroffen, um uns zu unterstützen.' WHERE entry=2000000626; /* You lack tact, Commander. This is not surprising. However, you are right in that the Horde will be forced to act. But not just the Horde. They already come - heroes from across the world. */
UPDATE dbscript_string SET content_loc3='Zu wenig und zu spät. Das wars. Bestenfalls werden wir gleich erledigt.' WHERE entry=2000000627; /* Too little, too late. The dam has broken. We are a bump in the road at best. */
UPDATE dbscript_string SET content_loc3='Warum seid Ihr also hier? Was sollen wir nur tun, wenn es keine Hoffnung mehr gibt?' WHERE entry=2000000628; /* So why are you here? What are we to do if there is no hope? */
UPDATE dbscript_string SET content_loc3='Es wird immer eins geben... Aschenbringer.' WHERE entry=2000000629; /* There is always... Ashbringer. */
UPDATE dbscript_string SET content_loc3='Deshalb seid Ihr also hergekommen, um das eine an Euch zu reißen, das die Existenz der Argentumdämmerung begründet und den Fall des Scharlachroten Kreuzzugs eingeläutet hat. Habe ich recht? Eure Worte öffnen alte Wunden, Kommandant!' WHERE entry=2000000630; /* So this is what you have come for? To try and claim that which is the reason for the Dawn\'s existence and the Crusade\'s downfall? You open old wounds with your words, Commander! */
UPDATE dbscript_string SET content_loc3='Der Aschenbringer ist nicht mehr und niemals wieder wird jemand mit seiner Stärke auf dieser Welt wandeln. Der junge Mograine ist nur ein Schatten des Mannes, der sein Vater einst war. Das wisst Ihr genauso gut wie ich, Kommandant! Dabei sollten wir nicht vergessen, dass es alleine Eure Führung war, die den Tod des Scharlachroten Hochlords Mograine verschuldet hat.' WHERE entry=2000000631; /* The Ashbringer is dead and there will never be another like him. The lesser Mograine is a far cry from the man his father was. You know this, Commander! It was, after all, your own leadership that was responsible for the death of the Scarlet Highlord Mograine. */
UPDATE dbscript_string SET content_loc3='Eure Führung brachte ein betrübtes Kind dazu, nach dem Schwert zu suchen. Ein Kind, das zu einem erwachsenen Mann heranreifte und dessen Herz einzig und alleine von purem Hass und dem Wunsch nach Rache beseelt war! Und wofür? Ihr seid dem Schwert keinen Schritt näher gekommen, als Ihr es vor fünf Jahren schon wart.' WHERE entry=2000000632; /* It was your leadership that manipulated a grieving child to try and recover the sword. A child that has grown into a man with nothing but vengeance and hatred in his heart! And for what? You are no closer to the sword now than you were five years ago. */
UPDATE dbscript_string SET content_loc3='Ihr glaubt also wirklich, dass, wenn Ihr Aschenbringer findet, Ihr den Verlauf des Krieges noch ändern könnt? Lasst mich Euch ein kleines Geheimnis anvertrauen, Kommandant: Die Macht des Aschenbringers lag nicht in seiner Klinge, sondern in dem Mann, der sie einst führte... Ich war dabei, Kommandant. Ich sah, wie er ganze Legionen von Untoten im Feuer der Gerechtigkeit verbrannte, lange bevor er seine Klinge überhaupt erst zog. Euer mangelndes Geschichtsverständnis... ist beklagenswert.' WHERE entry=2000000633; /* Yet you think that recovering Ashbringer will somehow turn the tide of battle? Let me let you in on a little secret, Commander: The power of the Ashbringer came from the man who would wield it... I was there, Commander. I watched him burn legions of undead in righteous fire before he would even unsheathe the blade. Alas, your grasp on history is ... lacking. */
UPDATE dbscript_string SET content_loc3='Werdet Euch gewahr, wo Ihr seid, und schweigt, bevor ich Lord Fordring und seine Ritter davon in Kenntnis setzen muss, dass Ihr hier nicht länger willkommen seid. Ich bin mir sicher, er würde Euch mit Freuden büßen sehen!' WHERE entry=2000000634; /* Now be silent and note where you are, lest I inform Lord Fordring and his knights that you are no longer here on amicable terms. I am certain he would take great pleasure in seeing to your \'atonement.\' */
UPDATE dbscript_string SET content_loc3='Ihr hattet die Fischplatte gewählt, Kommandant. Guten Appetit!' WHERE entry=2000000635; /* Ok, you had the fish platter, Commander. Enjoy! */
UPDATE dbscript_string SET content_loc3='Ihr hattet die Obstschale, nicht wahr? Vater?' WHERE entry=2000000636; /* Father, you had the bowl of fruit? Right? */
UPDATE dbscript_string SET content_loc3='Und für die Damen, gegrillter Babyeber. Guten Appetit!' WHERE entry=2000000637; /* And for the ladies, roast baby boar. Enjoy! */
UPDATE dbscript_string SET content_loc3='Meine Komplimente an den Chefkoch!' WHERE entry=2000000638; /* My compliments to the chef! */
UPDATE dbscript_string SET content_loc3='Ist das eine Mango? Erstaunlich!' WHERE entry=2000000639; /* Is this a mango? Stupendous! */
UPDATE dbscript_string SET content_loc3='Vielen Dank, Jessica.' WHERE entry=2000000640; /* Thank you, Jessica. */
UPDATE dbscript_string SET content_loc3='Bitte lasst es mich wissen, wenn ich noch etwas für Euch tun kann!' WHERE entry=2000000641; /* Please let me know if you need anything else! */
UPDATE dbscript_string SET content_loc3='Und ich möchte hinzufügen, dass dieses Kleid einfach bezaubernd ist! Ihr müsst mir unbedingt verraten, wo Ihr es erstanden habt.' WHERE entry=2000000642; /* And I might add, that dress is lovely! You must tell me where you got it from. */
UPDATE dbscript_string SET content_loc3='In der Tat... ein wundervolles Kleid, Jessica.' WHERE entry=2000000643; /* Indeed... Lovely dress, Jessica. */
UPDATE dbscript_string SET content_loc3='Amakkar, was meint Ihr, wohin wird uns Kriegsherr Bluthauer schicken? Ich bezweifle, dass ich noch eine Dienstzeit in der Steppe ertragen kann.' WHERE entry=2000000645; /* Amakkar, where do you think Warlord Goretooth is going to send us? I don\'t think I can handle another tour in the Steppes. */
UPDATE dbscript_string SET content_loc3='Hrmmm...' WHERE entry=2000000646; /* Hrmmm... */
UPDATE dbscript_string SET content_loc3='Stellt Euch nicht so an, Razal\'hieb. Benehmt Euch endlich wie ein Mitglied der Elitetruppe des Kriegshäuptlings. Ihr wollt doch nicht wie Gargal enden, oder?' WHERE entry=2000000647; /* Quit being such a baby, Razal\'blade. It\'s time to start acting like you\'re part of one of the Warchief\'s elite forces. You don\'t want to end up like Gargal, do you? */
UPDATE dbscript_string SET content_loc3='%s scheint keinerlei Kommentar zur Kenntnis zu nehmen.' WHERE entry=2000000648; /* %s seems oblivious to the nearby commentary. */
UPDATE dbscript_string SET content_loc3='Ehrenhauer!' WHERE entry=2000000649; /* Proudtusk! */
UPDATE dbscript_string SET content_loc3='Nein, Gargal. Schlicht und ergreifend nein.' WHERE entry=2000000650; /* No, Gargal. Just no. */
UPDATE dbscript_string SET content_loc3='Aber...' WHERE entry=2000000651; /* But... */
UPDATE dbscript_string SET content_loc3='NEIN! Psst! Klappe halten. Bluthauer kommt!' WHERE entry=2000000652; /* NO! Shhh! Just shut up. Goretooth is coming! */
UPDATE dbscript_string SET content_loc3='%s lacht.' WHERE entry=2000000653; /* %s laughs. */
UPDATE dbscript_string SET content_loc3='IM GLEICHSCHRITT, IHR MADEN!' WHERE entry=2000000654; /* FALL IN, MAGGOTS! */
UPDATE dbscript_string SET content_loc3='Alle anwesend und durchgezählt, SIR!' WHERE entry=2000000655; /* All present and accounted for, SIR! */
UPDATE dbscript_string SET content_loc3='Also gut, Ehrenhauer, Ihr könnt Euren Marsch beginnen. Los geht\'s!' WHERE entry=2000000656; /* Very well, Proudtusk, you may begin your march. Carry on! */
UPDATE dbscript_string SET content_loc3='SIR, JA, SIR!' WHERE entry=2000000657; /* SIR, YES SIR! */
UPDATE dbscript_string SET content_loc3='Ihr habt den Kriegsherrn gehört, ihr Maden! MARSCH, MARSCH!' WHERE entry=2000000658; /* You heard the Warlord, maggots! MARCH! */
UPDATE dbscript_string SET content_loc3='Oh, das ist alles schrecklich, schrecklich schlimm! Ihr werdet STERBEN, wenn Ihr es seht, Vivian.' WHERE entry=2000000659; /* Oh, it\'s just terribly, terribly awful! You will just DIE when you see it, Vivian. */
UPDATE dbscript_string SET content_loc3='Oh, erzählt schon! Diese Spannung ist unerträglich!' WHERE entry=2000000660; /* Oh, do tell! The suspense alone is enough to make a girl scream! */
UPDATE dbscript_string SET content_loc3='Tja, soweit ich gehört habe, hat die neue Seuche den VOLLEN Geschmack der alten, nicht aber ihre Kalorien oder Kohlehydrate!' WHERE entry=2000000661; /* Well, from what I hear, the new plague has ALL of the old plague flavor but none of the old plague calories or carbohydrates! */
UPDATE dbscript_string SET content_loc3='%s klatscht aufgeregt in die Hände.' WHERE entry=2000000662; /* %s claps excitedly. */
UPDATE dbscript_string SET content_loc3='Oh, wie überaus entzückend!' WHERE entry=2000000663; /* Oh, how positively delightful! */
UPDATE dbscript_string SET content_loc3='Ah, guten Tag, die Damen. Schon von der neuen Seuche gehört?' WHERE entry=2000000664; /* Well hello there ladies. Heard about the new plague? */
UPDATE dbscript_string SET content_loc3='Iiiiiiiih... nicht mal, wenn Ihr der letzte Orc auf ganz Kalimdor wärt, Lunk.' WHERE entry=2000000665; /* Ewwwww... not if you were the last orc on Kalimdor, Lunk. */
UPDATE dbscript_string SET content_loc3='%s errötet.' WHERE entry=2000000666; /* %s blushes. */
UPDATE dbscript_string SET content_loc3='%s lacht.' WHERE entry=2000000667; /* %s laughs.. */
UPDATE dbscript_string SET content_loc3='Oh, so spät schon! Ich muss zu meinem Posten zurückkehren... da drüben.' WHERE entry=2000000668; /* Ah, look at the time! I must return to my post... up over there. */
UPDATE dbscript_string SET content_loc3='Wenn Ihr mich jetzt bitte entschuldigen wollt, ich muss gehen. Guten Tag, die Damen.' WHERE entry=2000000669; /* If you\'ll excuse me, I must take my leave. Good day, ladies. */
UPDATE dbscript_string SET content_loc3='%s blättert hastig durch den dicken Folianten.' WHERE entry=2000000786; /* %s wildly flips through the pages of a large tome. */
UPDATE dbscript_string SET content_loc3='Welch herrliche Ironie! Die Truppen von Sturmwind werden bei der Verteidigung ihrer Stadt aufgerieben und erstehen wieder als Werkzeuge der Zerstörung, um an der Seite der Bruderschaft zu kämpfen.' WHERE entry=2000000787; /* The sweet irony -- Stormwind forces cut down in defense of their city, only to rise and fight alongside the Brotherhood as instruments of destruction. */
UPDATE dbscript_string SET content_loc3='Wenn einer stirbt, treten zwei an seine Stelle!' WHERE entry=2000000788; /* And should one die, two shall rise! */
UPDATE dbscript_string SET content_loc3='%s blättert durch die Bücher.' WHERE entry=2000000789; /* %s shuffles through the books. */
UPDATE dbscript_string SET content_loc3='Wo habe ich denn bloß die Formeln der Venture Company hingelegt?' WHERE entry=2000000790; /* Now where did I put the Venture Company formulations? */
UPDATE dbscript_string SET content_loc3='He, Krähe...' WHERE entry=2000000791; /* Hey, Raven... */
UPDATE dbscript_string SET content_loc3='Ja?' WHERE entry=2000000792; /* Yea? */
UPDATE dbscript_string SET content_loc3='Können wir eine Pause machen? Meine Füße bringen mich noch um und diese... Dinger da unten finde ich unheimlich.' WHERE entry=2000000793; /* Can we take a break? My feet are killing me and those ... those things down there are creeping me out. */
UPDATE dbscript_string SET content_loc3='Ich weiß nicht, Jill. Wenn uns Klaven erwischt, während wir hier dösen, gehören wir vielleicht selbst bald zu diesen Dingern!' WHERE entry=2000000794; /* I dunno, Jill. If Klaven comes down while we\'re slacking, we may end up as one of those things! */
UPDATE dbscript_string SET content_loc3='Klaven zahlt mir nicht genug, als dass ich mich mit Zombies, Drohnen oder Untoten oder wie auch immer er die Dinger nennt, herumschlagen würde.' WHERE entry=2000000795; /* Klaven doesn\'t pay me enough to deal with those zombies, drones or whatever he calls \'em. I don\'t know, Jill. If Klaven comes down while we\'re slacking, we might end up as those things! */
UPDATE dbscript_string SET content_loc3='*Seufz* Du hast wahrscheinlich Recht, Krähe. Ich dreh dann mal wieder meine Runde...' WHERE entry=2000000796; /* *Sigh* You\'re probably right, Raven. I\'m going back on patrol.... */
UPDATE dbscript_string SET content_loc3='Sprecht mit mir, um Euch meine Geschichte anzuhören.' WHERE entry=2000000813; /* Speak with me to hear my tale. */
UPDATE dbscript_string SET content_loc3='Seid gegrüßt, sterbliche Wesen! Ich wurde von den Scheiben von Norgannon aktiviert, um Euch zu helfen!' WHERE entry=2000000817; /* Greetings, mortals! I have been activated by the Discs of Norgannon to assist you! */
UPDATE dbscript_string SET content_loc3='Eure Scheiben sind jetzt bereit! Benutzt die Scheiben von Norgannon abermals, um sie zu holen!' WHERE entry=2000000818; /* The discs are now ready! Engage the Discs of Norgannon once again to retrieve them! */
UPDATE dbscript_string SET content_loc3='Endlich haben sie Erbarmen! Sie haben uns etwas zu trinken gegeben!' WHERE entry=2000001033; /* Finally they show mercy!  They\'ve given us something to drink! */
UPDATE dbscript_string SET content_loc3='Und auch noch Kniegräberstarkbier! Dem Licht sei Dank! Ich war schon ganz ausgedörrt!' WHERE entry=2000001034; /* And Shindigger Stout too!  Thank the Light!  I was parched! */
UPDATE dbscript_string SET content_loc3='NEFARIAN IST TOT! Bürger von Orgrimmar, verbeuget euch vor der Macht von $n und $g seinen:ihren; Verbündeten, die es geschafft haben, dem schwarzen Drachenschwarm einen schweren Schlag zu versetzen, der die Aspekte sicherlich wieder aufrütteln wird. Diesesmal spürt der Anführer des schwarzen Drachenschwarms diese Niederlage bitterlich: Todesschwinge windet sich vor Schmerzen und Qualen!' WHERE entry=2000001035; /* NEFARIAN IS SLAIN! People of Orgrimmar, bow down before the might of $n and $g his:her; allies for they have laid a blow against the Black Dragonflight that is sure to stir the Aspects from their malaise! This defeat shall surely be felt by the father of the Black Flight: Deathwing reels in pain and anguish this day! */
UPDATE dbscript_string SET content_loc3='Erfreut euch an der mutigen Tat von $n! Lasst $g seinen:ihren; Siegesschrei eure Herzen höher schlagen!' WHERE entry=2000001036; /* Be lifted by $n\'s accomplishment! Revel in $g his:her; rallying cry! */
UPDATE dbscript_string SET content_loc3='Angehörige der Allianz, der Herrscher des Schwarzfels wurde besiegt! Die vereinte Macht von $n und $g seiner:ihrer; Verbündeten bezwang Nefarian!' WHERE entry=2000001037; /* Citizens of the Alliance, the Lord of Blackrock is slain! Nefarian has been subdued by the combined might of $n and $g his:her; allies! */
UPDATE dbscript_string SET content_loc3='Erhebt Eure Gemüter! Feiert $g Euren Helden:Eure Heldin;, erfreut Euch an $g seinem:ihrem; Ruhm! Lasst den Siegesschrei des Drachentöters Eure Herzen höher schlagen!' WHERE entry=2000001038; /* Let your spirits rise! Rally around your champion, bask in $g his:her; glory! Revel in the rallying cry of the dragon slayer! */
UPDATE dbscript_string SET content_loc3='Möge diese böse Aura von diesem Artefakt verbannt werden!' WHERE entry=2000001039; /* May this evil aura be banished from this artifact! */
UPDATE dbscript_string SET content_loc3='Ich kann fühlen, dass die Anwesenheit des Bösen schwindet...' WHERE entry=2000001040; /* I can feel the presence of evil leaving... */
UPDATE dbscript_string SET content_loc3='%s beginnt das Ritual.' WHERE entry=2000001116; /* %s begins the ritual. */
UPDATE dbscript_string SET content_loc3='%s sammelt das Material zusammen und beginnt zu arbeiten.' WHERE entry=2000001117; /* %s gathers the materials together and begins to work. */
UPDATE dbscript_string SET content_loc3='Ich bin mit den Waffen fertig, $n.' WHERE entry=2000001118; /* I have completed the weapons, $n. */
UPDATE dbscript_string SET content_loc3='An die Arbeit, $n!' WHERE entry=2000001143; /* Let\'s get to work, $n! */
UPDATE dbscript_string SET content_loc3='Ich danke dem Licht für diese Segnung. Möge sie für Gerechtigkeit und die Verteidigung aller guten Kreaturen eingesetzt werden.' WHERE entry=2000001144; /* I thank the Light for this blessing. May it be used for justice and to defend all good creatures. */
UPDATE dbscript_string SET content_loc3='Bitte sehr, $n.' WHERE entry=2000001145; /* Here you are, $n. */
UPDATE dbscript_string SET content_loc3='Bravo! Bravo! Ausgezeichnet! Was denn? Dachtet Ihr etwa, ich sei tot?' WHERE entry=2000001336; /* Bravo! Bravo! Good show. What? You thought I was dead? */
UPDATE dbscript_string SET content_loc3='Ihr Narren! Ich wusste, wenn ich auf die kümmerlichen Gefühle meines Bruders setze, würde er \'Retter\' schicken.' WHERE entry=2000001337; /* Fools. I knew that if I played upon my brother\'s feeble emotions, he would send \'rescuers.\' */
UPDATE dbscript_string SET content_loc3='Es war ein Kinderspiel, Euch dazu zu bringen, diesen Schwachköpfen des Schattenhammers den letzten Suntarastein abzuluchsen.' WHERE entry=2000001338; /* How easy it was to manipulate you into recovering the last Suntara stone from those imbeciles of the Twilight\'s Hammer. */
UPDATE dbscript_string SET content_loc3='Als ich in Rußschlacks Grabungsstätte auf die Suntarasteine stieß, durchströmte mich die Macht von Ragnaros. Ragnaros gab meinem Leben einen Sinn.' WHERE entry=2000001339; /* When I stumbled upon the Suntara stones at the Grimesilt Digsite, the power of Ragnaros surged through my being. It was Ragnaros that gave me a purpose. */
UPDATE dbscript_string SET content_loc3='Die Schaffung Obsidions war Ragnaros\' Wille. Obsidion wird die Schwarzfelsorcs von der Schwarzfelsspitze vernichten und uns mit unseren Brüdern in den feurigen Tiefen vereinigen.' WHERE entry=2000001340; /* It was the will of Ragnaros that Obsidion be built. Obsidion will destroy the Blackrock orcs of Blackrock Spire, uniting us with our bretheren in the fiery depths. */
UPDATE dbscript_string SET content_loc3='Und schließlich war es auch Ragnaros, der mir meinen Namen gab, als ich als Akolyth des Feuers wiedergeboren wurde: Lathoric... Lathoric der Schwarze.' WHERE entry=2000001341; /* And ultimately, it was Ragnaros that named me when I was reborn as an acolyte of fire: Lathoric... Lathoric the Black. */
UPDATE dbscript_string SET content_loc3='Das Lachen von %s verklingt...' WHERE entry=2000001342; /* %s laughter trails off */
UPDATE dbscript_string SET content_loc3='Warum kümmern wir uns nicht gleich jetzt um Euch, Hendel? Lady Prachtmeer wünscht, Euch im Turm zu sprechen.' WHERE entry=2000001343; /* Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower. */
UPDATE dbscript_string SET content_loc3='Bitte... bitte... Lady Prachtmeer. Ich wollte doch nicht...' WHERE entry=2000001344; /* Please... please... Miss Proudmore. I didn\'t mean to... */
UPDATE dbscript_string SET content_loc3='Ich muss mich entschuldigen, dass ich so lange gebraucht habe, um hierher zu kommen. Ich wollte, dass Lady Prachtmeer ebenfalls anwesend ist.' WHERE entry=2000001345; /* I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also. */
UPDATE dbscript_string SET content_loc3='Wir können nur noch kurz bleiben, dann müssen wir zum Turm zurück. Wenn Ihr noch mehr mit uns besprechen wollt, könnt Ihr uns dort antreffen.' WHERE entry=2000001346; /* We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there. */
UPDATE dbscript_string SET content_loc3='Macht\'s gut. Wir werden uns gewiss wiedersehen.' WHERE entry=2000001347; /* Farewell. We shall speak again. I\'m sure. */
UPDATE dbscript_string SET content_loc3='%s brutzelt eine Ladung scharfer Bussardflügel für $n.' WHERE entry=2000001453; /* %s cooks up a batch of spicy hot buzzard wings for $N. */
UPDATE dbscript_string SET content_loc3='Opus möchte etwas von Cuergos Gold... mit Wurm.' WHERE entry=2000005001; /* Opus wants some Cuergo\'s Gold... with worm. */
UPDATE dbscript_string SET content_loc3='Bijou ist verloren! KRÄCHZ! *hust*' WHERE entry=2000005002; /* Bijou lost! SQUAWK! *cough */
UPDATE dbscript_string SET content_loc3='Kibler wird vom Fantasy-Tierschutz untersucht.' WHERE entry=2000005003; /* Kibler is being investigated by G.E.T.A. */
UPDATE dbscript_string SET content_loc3='Bijou weiß, wo Schätze verborgen sind.' WHERE entry=2000005004; /* Bijou knows where treasures are hidden. */
UPDATE dbscript_string SET content_loc3='Großdrachen kontrollieren Orcs!? KRÄCHZ!' WHERE entry=2000005005; /* Dragons controlling orcs!? SQUAWK! */
UPDATE dbscript_string SET content_loc3='Alle gehen zur Spitze, aber keiner kommt zurück... genau wie Bijou.' WHERE entry=2000005006; /* They all go to the Spire but none never come back... just like Bijou. */
UPDATE dbscript_string SET content_loc3='Fragt Kibler nach dem Schatz! KRÄCHZ!' WHERE entry=2000005007; /* Ask Kibler about the treasure! SQUAWK! */
UPDATE dbscript_string SET content_loc3='KRÄCHZ!!! *hust* *hust* Ich muss mit dem Rauchen aufhören!' WHERE entry=2000005008; /* SQUAWK!! *cough* *cough* I need to quit smoking! */
UPDATE dbscript_string SET content_loc3='Gut gemacht, Soldaten! Rührt euch!' WHERE entry=2000005009; /* Well done, soldiers! At ease! */
UPDATE dbscript_string SET content_loc3='Zug zug! Freut sich zu helfen!' WHERE entry=2000005010; /* Zug zug! Happy to help! */
UPDATE dbscript_string SET content_loc3='Ja auf der Stelle Mama!' WHERE entry=2000005011; /* Yes right away momma! */
UPDATE dbscript_string SET content_loc3='Der Alptraum von Ursal ist vorüber! Wir sind frei!' WHERE entry=2000005012; /* The nightmare of Ursal is over! We are free! */
UPDATE dbscript_string SET content_loc3='Ah, das muss er jetzt aber sein... nein? Pah!' WHERE entry=2000005018; /* Ah, this must be him now... no? */
UPDATE dbscript_string SET content_loc3='Ah, Freiheit! Wenn auch kurz, ist sie wunderbar...' WHERE entry=2000005024; /* Ah, freedom! Although brief, so sweet it is... */
UPDATE dbscript_string SET content_loc3='Die Macht des Lichkönigs wird stärker. Es ist von äußerster Wichtigkeit, dass unser Zeitplan seinem Vorhaben entspricht!' WHERE entry=2000005027; /* The Lich King\'s forces are building. It is imperative that our timetable supports his plans. */
UPDATE dbscript_string SET content_loc3='Morgen beginnen wir mit der Ausbildung unserer viel versprechenden Großdrachen, also vergesst Eure Kauknochen nicht.' WHERE entry=2000005028; /* Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys. */
UPDATE dbscript_string SET content_loc3='Selbst unser ältestes Großdrachengelege ist noch weit von der Reife entfernt, aber wir sind überzeugt, dass der Drachenschwarm bald so weit sein wird, wenn wir nur Geduld haben und unsere Studien sorgfältig fortsetzen.' WHERE entry=2000005029; /* Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragons will soon be ready. */
UPDATE dbscript_string SET content_loc3='Ich komme aus dem Land im Süden... wo Männer leiden und Frauen wüten.' WHERE entry=2000005031; /* I come from the land down under... where women plague and men sunder. */
UPDATE dbscript_string SET content_loc3='...Und dann sagte der Taure, "33 ZENTIMETER!"' WHERE entry=2000005032; /* ...And then the tauren said, "13 INCHES!" */
UPDATE dbscript_string SET content_loc3='Er entkommt!' WHERE entry=2000005035; /* It\'s getting away! */
UPDATE dbscript_string SET content_loc3='Nun, die Pflicht ruft, nicht wahr?. Zuerst die Glocke...um Euch Kraft zu verleihen!' WHERE entry=2000005038; /* Well duties call, yes? First, the bell... to give you vigor! */
UPDATE dbscript_string SET content_loc3='Beim nächsten Mal werde ich das Rad platzieren... um Euch vor Schaden zu bewahren.' WHERE entry=2000005050; /* Next, I\'ll place the wheel... to protect you from harm. */
UPDATE dbscript_string SET content_loc3='Heulsuse! Heulsuse!' WHERE entry=2000005057; /* Crybaby! Crybaby! */
UPDATE dbscript_string SET content_loc3='Das Baby will seine Puppe!' WHERE entry=2000005058; /* Baby wants her dolly! */
UPDATE dbscript_string SET content_loc3='Ich frage mich, ob deine Puppe schwimmen kann!' WHERE entry=2000005059; /* I wonder if your dolly can swim! */
UPDATE dbscript_string SET content_loc3='Haha! Ich habe Betsy!' WHERE entry=2000005060; /* Ha ha! I have Betsy! */
UPDATE dbscript_string SET content_loc3='Gib mir meine Puppe!!!' WHERE entry=2000005061; /* I have your dolly! Nyah nyah! */
UPDATE dbscript_string SET content_loc3='Gib mir meine Puppe!!!' WHERE entry=2000005062; /* Gimmie my dolly!! */
UPDATE dbscript_string SET content_loc3='Tu Betsy nicht weh, du Widerling!' WHERE entry=2000005063; /* Don\'t hurt Betsy, you meanie! */
UPDATE dbscript_string SET content_loc3='Habt Ihr Drull schon befreit? Ich weiß nicht, wo er festgehalten wird.' WHERE entry=2000005078; /* Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE dbscript_string SET content_loc3='Ich werde Euch nur aufhalten, aber kein rosahäutiger Mensch kann mir jetzt noch meine Freiheit nehmen!' WHERE entry=2000005079; /* I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE dbscript_string SET content_loc3='Ah, es ist gut, von meinen Fesseln befreit zu sein! Habt Ihr Togthar schon befreit? Die Menschen haben uns nach der Gefangennahme getrennt.' WHERE entry=2000005081; /* Ah, it is good to be free of my bonds! Have you freed Tog\'thar yet? The humans split us up after our capture. */
UPDATE dbscript_string SET content_loc3='Geht ohne mich voraus! Ich werde allein nach Tarrens Mühle reisen.' WHERE entry=2000005082; /* Go on ahead without me! I will make my own way to Tarren Mill. */
UPDATE dbscript_string SET content_loc3='Holt ihn da raus!' WHERE entry=2000005099; /* Get him out of there! */
UPDATE dbscript_string SET content_loc3='Vielleicht wird Ograbisi Euren Kopf als kleinen Hut benutzen, wenn ich mit Euch fertig bin.' WHERE entry=2000005100; /* Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE dbscript_string SET content_loc3='Wo ich herkomme, kriegt man Haue, wenn man die Zellentür eines Mitgefangenen öffnet!' WHERE entry=2000005101; /* Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE dbscript_string SET content_loc3='Ograbisi braucht neuen Hut!' WHERE entry=2000005102; /* Ograbisi needs new hat. */
UPDATE dbscript_string SET content_loc3='Jack und Jill meine Runzligen. Mir gehört all das Wasser um mich herum.' WHERE entry=2000005103; /* Jack and Jill my wrinkled patoot! I do all the water luggin\' \'round here. */
UPDATE dbscript_string SET content_loc3='Wo ist das Wasser, Emma? Hast du Wasser geholt, Emma? Wenn ich nicht wäre, würd\' das Pack gar nicht wissen, wie Wasser aussieht.' WHERE entry=2000005104; /* Where\'s the water, Emma? Get the water, Emma? If\'n it weren\'t fer me, that lot wouldn\'t know what water looks like. */
UPDATE dbscript_string SET content_loc3='Natürlich rede ich mit mir selbst. Das ist die einzige Möglichkeit, in dieser Stadt ein nettes Gespräch zu führen.' WHERE entry=2000005105; /* O\'ourse I\'m talking to myself. Only way to get a decent conversation in this city. */
UPDATE dbscript_string SET content_loc3='Als ob ich auf meine alten Tage nichts Besseres zu tun hätte, als Wassereimer zu schleppen.' WHERE entry=2000005106; /* As if I don\'t have better things to do in my old age than carry buckets of water. */
UPDATE dbscript_string SET content_loc3='Mir scheint\'s, als ginge ich hundert Mal am Tag den ganzen Weg zum Brunnen, um immer noch mehr Wasser zu holen. Keinen Respekt vor den Alten, ich sag\'s Euch.' WHERE entry=2000005107; /* Seems like a hundred times a day I walk all the way to get more water. No respect for their elders I tell ya. */
UPDATE dbscript_string SET content_loc3='Déjà-vu. Für einen Moment dachte ich, ich wäre zu Hause... vor der Seuche...' WHERE entry=2000005108; /* Deja vu. For a moment I thought I was back home... before the plague... */
UPDATE dbscript_string SET content_loc3='Sie sagen er kann sich manchmal in einen Raben verwandeln.' WHERE entry=2000005109; /* They say he can turn into a raven sometimes. */
UPDATE dbscript_string SET content_loc3='Sie sagen er kann sich manchmal in einen Raben verwandeln.' WHERE entry=2000005110; /* They say he can turn into a raven sometimes. */
UPDATE dbscript_string SET content_loc3='Und dann biss ihm der Hase einfach den Kopf ab... Ich schwör\'s.' WHERE entry=2000005111; /* And then the rabbit just bit his head off... I swear. */
UPDATE dbscript_string SET content_loc3='Ich schwöre es, man hat sie tatsächlich schon gesehen. Es gibt die Pandaren wirklich!' WHERE entry=2000005112; /* I swear, people have actualy seen them. Pandaren really do exist!! */
UPDATE dbscript_string SET content_loc3='Es gibt einen Grund, warum die Augen von Orcs rot glühen! Sie trinken Blut!' WHERE entry=2000005113; /* You know why Orcs eyes glow red? It\'s because they drink blood! */
UPDATE dbscript_string SET content_loc3='Ich glaube nicht, dass es in diesen Kanälen Fische gibt.' WHERE entry=2000005114; /* I dont think there\'s any fish in these canals. */
UPDATE dbscript_string SET content_loc3='Genau so hat Lothar mit bloßen Händen sechsunddreißig Orcs getötet!' WHERE entry=2000005115; /* And that\'s how Lother killed thirty six orcs with his bare hands! */
UPDATE dbscript_string SET content_loc3='Iiieeh... das ist kein Fisch!' WHERE entry=2000005116; /* Eww... that\'s not a fish! */
UPDATE dbscript_string SET content_loc3='Ich habe Wurmstückchen auf meinen Schuhen...' WHERE entry=2000005117; /* I got worm guts on my shoes. */
UPDATE dbscript_string SET content_loc3='Ich glaube, ich sehe da was.' WHERE entry=2000005118; /* I think i see something. */
UPDATE dbscript_string SET content_loc3='Iiieeh... das ist kein Fisch!' WHERE entry=2000005119; /* Eww... that\'s not a fish! */
UPDATE dbscript_string SET content_loc3='Ich glaube, ich sehe da was.' WHERE entry=2000005120; /* I think i see something. */
UPDATE dbscript_string SET content_loc3='Ich hoffe, das war ein Fisch!' WHERE entry=2000005121; /* I hope that was a fish! */
UPDATE dbscript_string SET content_loc3='Wurm kommt an den Haken, Haken kommt ins Wasser. Fisch ist im Wasser, unser Fisch.' WHERE entry=2000005122; /* Worm goes on the hook, hook goes in the water, Fish is in the water, our fish. */
UPDATE dbscript_string SET content_loc3='Kannst du dir das vorstellen?' WHERE entry=2000005123; /* Can you imagine? */
UPDATE dbscript_string SET content_loc3='Wirklich?' WHERE entry=2000005124; /* Really? */
UPDATE dbscript_string SET content_loc3='Wow.' WHERE entry=2000005125; /* Wow. */
UPDATE dbscript_string SET content_loc3='Mein Vater sagt, das ist nur eine Geschichte.' WHERE entry=2000005126; /* My father says that\'s just a story. */
UPDATE dbscript_string SET content_loc3='Wirklich?' WHERE entry=2000005127; /* Really? */
UPDATE dbscript_string SET content_loc3='Wow.' WHERE entry=2000005128; /* Wow. */
UPDATE dbscript_string SET content_loc3='Klasse!' WHERE entry=2000005129; /* That\'s neat. */
UPDATE dbscript_string SET content_loc3='Wow.' WHERE entry=2000005130; /* Wow. */
UPDATE dbscript_string SET content_loc3='Wirklich?' WHERE entry=2000005131; /* Really? */
UPDATE dbscript_string SET content_loc3='Rührt euch, Hunde! Wir haben hier etwas zu erledigen!' WHERE entry=2000005132; /* Look alive, dogs! We got an operation to run here! */
UPDATE dbscript_string SET content_loc3='Hier haben wir die Kathedrale des Lichts, Sturmwinds Zentrum spiritueller Erleuchtung.' WHERE entry=2000005134; /* Here we have the Cathedral of Light, the center of spiritual enlightenment here in Stormwind. */
UPDATE dbscript_string SET content_loc3='Ja, das ist wahr. Sowohl Paladine als auch Priester üben ihre Fertigkeiten und erforschen die großen Wahrheiten hinter den Mauern der Kathedrale.' WHERE entry=2000005135; /* Yes, that is true. Paladins and Priests alike train their skills and research great truths behind the walls of the Cathedral. */
UPDATE dbscript_string SET content_loc3='Wenn ihr mir bitte folgen wollt, Kinder. Wir werden gleich die Burg sehen, in der König Varian höchstpersönlich auf seinem Thron sitzt.' WHERE entry=2000005136; /* Children if you would please follow me, we will now be going to see the keep where King Anduin Wrynn himself sits on this throne. */
UPDATE dbscript_string SET content_loc3='Hier haben wir Burg Sturmwind. Gebaut auf den Ruinen von Schloss Sturmwind, das während des ersten Großen Kriegs von der Horde zerstört wurde.' WHERE entry=2000005137; /* Here we have Stormwind Keep. Built upon the ruins of Stormwind Castle, which was destroyed by the Horde in the first Great War. */
UPDATE dbscript_string SET content_loc3='Als die Horde zerschlagen wurde, kehrten die Mannen hierher zurück und begannen, diese einst so herrliche Stadt als Beweis unseres eigenen Überlebens wieder aufzubauen.' WHERE entry=2000005138; /* When the Horde was shattered, men returned here and began to rebuild the once great city as a testament to our own survival. */
UPDATE dbscript_string SET content_loc3='Ja, gut... lasst uns jetzt zu dem Monument gehen, das den Helden der beiden Großen Kriege gewidmet ist, dem Tal der Helden. Folgt mir.' WHERE entry=2000005139; /* Yes, well...let\'s head on to the monument dedicated to the heroes of the two Great Wars, the Valley of Heroes. Follow Me. */
UPDATE dbscript_string SET content_loc3='Ist das nicht erstaunlich, Kinder? Jeder, der die Stadt betritt, muss die wachsamen Augen der größten Helden unseres Landes passieren.' WHERE entry=2000005140; /* Isn\'t it amazing, children? All who enter the city must walk beneath the watchful eyes of the greatest heroes of our lands. */
UPDATE dbscript_string SET content_loc3='Atemberaubend. Kinder, wenn wir in der Schule zurück sind, wird jeder von euch einen mündlichen Bericht über eine dieser Legenden abgeben.' WHERE entry=2000005141; /* Breathtaking. Children, when we return to the school, you will each give an oral report on one of these legendary people. */
UPDATE dbscript_string SET content_loc3='Werft noch einen Blick darauf, bevor wir zum heiligen Distrikt und zur großen Kathedrale des Lichts aufbrechen.' WHERE entry=2000005142; /* Now, take another long look before we make our way to the Holy District and the great Cathedral of Light. */
UPDATE dbscript_string SET content_loc3='Wenn wir die Essenzströme umkehren, können wir vielleicht auch die Polarität ändern.' WHERE entry=2000005148; /* If we reverse the Essence flows perhaps we can alter the polarity. */
UPDATE dbscript_string SET content_loc3='Warum können wir das Feld nicht einfach verlagern, um die Abweichung im Fluxus zu kompensieren?' WHERE entry=2000005149; /* Why can\'t we just shift the array to compensate for the variance in the flux? */
UPDATE dbscript_string SET content_loc3='Die Magiefelder würden sich dann also in eine Kettenreaktion positiv aufgeladener Energien verwandeln.' WHERE entry=2000005150; /* So then the array of magics would cascade into a chain reaction of positively charged energies. */
UPDATE dbscript_string SET content_loc3='Die sich daraus ergebenden Energien können dennoch kollabieren, und dies könnte eine Energieströmung hervorrufen, die Euch eine Migräne für Wochen bescheren würde.' WHERE entry=2000005151; /* The magical wards at that sholud be supported enough by the energy flux to certain the entity. */
UPDATE dbscript_string SET content_loc3='Maginor sagt, dass Wechselströme positiver Energie in der Tat einen viel stabileren Fluss erzeugen.' WHERE entry=2000005152; /* Maginor says that twisting alternating flows of positive energy actually creates a much more stable flow. */
UPDATE dbscript_string SET content_loc3='Das Portal würde sich leicht verlagern. Wenn Ihr zu diesem Zeitpunkt einen Bindezauberspruch sprecht, könnt Ihr es an Ort und Stelle verfestigen.' WHERE entry=2000005153; /* The portal will shift slightly, at this point if you cast a binding cantrip you will solidify it in place.. */
UPDATE dbscript_string SET content_loc3='Wenn die positiv ausgerichteten Energien mit den negativ aufgeladenen Energien kollidieren, heben sie einander nicht auf.' WHERE entry=2000005154; /* When the positively aligned energies collide with the negatively charged energies, they don\'t negate one another. */
UPDATE dbscript_string SET content_loc3='Zwölf Fokuszentren einzubringen könnte es den magischen Energien erlauben, sich stabiler zu formen, da sie die notwendige Stabilität einbringen.' WHERE entry=2000005155; /* Putting in twelve centers of focus might allow the magical energies to form more solidly, adding the necessary stability. */
UPDATE dbscript_string SET content_loc3='Ich denke nicht. Ich möchte nicht wieder hochgejagt werden.' WHERE entry=2000005156; /* I think not, I don\'t need to be blown up again. */
UPDATE dbscript_string SET content_loc3='Ich denke, dass es funktionieren könnte, wenn wir zum Zaubern zwanzig Leute zur Verfügung hätten.' WHERE entry=2000005157; /* I suppose that could work, if we had twenty people to cast it with. */
UPDATE dbscript_string SET content_loc3='Würde das nicht ein Kreuzen der Strahlen bedeuten? Ist das nicht ziemlich schlimm?' WHERE entry=2000005158; /* But wouldn\'t that mean crossing the streams? Isn\'t that really bad?? */
UPDATE dbscript_string SET content_loc3='Aber was passiert, wenn die resultierende Frequenzverschiebung eine magische Rückkopplung durch die Ströme schickt?' WHERE entry=2000005159; /* But what if the resulting frequency shift were to send magical feedback up the flows? */
UPDATE dbscript_string SET content_loc3='Aber ist es nicht genau das, was die anfänglichen Probleme mit Adept Sylerias magischen Formeln verursachte?' WHERE entry=2000005160; /* But isn\'t that what caused the initial problems with Adept Syleria\'s magical formula? */
UPDATE dbscript_string SET content_loc3='Das wird jedoch den magischen Fokus aus der Balance bringen und im Äther eine umgekehrte Vibration verursachen.' WHERE entry=2000005161; /* That will unbalance the magical focus, though, and cause a reverse vibration in the ether. */
UPDATE dbscript_string SET content_loc3='Würde das nicht bewirken, dass die Bindung sich löst, wenn sie nicht vor Beginn des Zauberns richtig verankert wurde?' WHERE entry=2000005162; /* Wouldn\'t that cause the weave to unravel if not properly anchored before starting the casting? */
UPDATE dbscript_string SET content_loc3='Die resultierenden Energien könnten natürlich kollabieren und das könnte wiederum einen Energiefluss erzeugen, der Euch die nächsten Wochen eine Migräne bescheren würde.' WHERE entry=2000005163; /* The resultant energies could collapse though, and that could cause an energy flux that would give you a migraine for weeks. */
UPDATE dbscript_string SET content_loc3='Zumindest wären wir dann nicht da, um sauber machen zu müssen.' WHERE entry=2000005164; /* At least we wouldn\'t be around to have to clean it up. */
UPDATE dbscript_string SET content_loc3='Hui, und das alles für einen Liebestrank. Hoffentlich ist er es wert.' WHERE entry=2000005165; /* Wow, all of this for a love potion. Hope he\'s worth it.. */
UPDATE dbscript_string SET content_loc3='Nur, wenn wir nicht die richtigen Initialisierungsprozeduren befolgen.' WHERE entry=2000005166; /* Only if we didn\'t follow the proper initialization procedures. */
UPDATE dbscript_string SET content_loc3='Stimmt es, dass die Paladine hier trainieren?' WHERE entry=2000005167; /* Is it true that the paladins train here? */
UPDATE dbscript_string SET content_loc3='Warum müssen wir das hier überhaupt lernen?' WHERE entry=2000005168; /* Why do we have to learn this stuff anyway? */
UPDATE dbscript_string SET content_loc3='Was wäre denn, wenn wir drei Fokusse nach tyreanischem Muster einsetzten? Das sollte die Lösung sein.' WHERE entry=2000005169; /* What if we used three focuses in Tyrean pattern? That should solve it. */
UPDATE dbscript_string SET content_loc3='Seid nicht immer so negativ. Die Chancen, dass das eintritt, stehen zwischen Null und Nichts.' WHERE entry=2000005170; /* Always so negative. The chances of that happening are between zero and none. */
UPDATE dbscript_string SET content_loc3='Wenn wir die surianische Theorie benutzen, dann ja, aber nicht, wenn wir den y\'serianischen Ansatz verfolgen.' WHERE entry=2000005171; /* If we use the Surian theory, then yes, but not if we go with the Y\'serian approach. */
UPDATE dbscript_string SET content_loc3='Aber wenn wir es mit einem Ankergewinde auf angemessenem Energiefluss stabilisieren könnten, sollte es funktionieren.' WHERE entry=2000005172; /* But if we stabilize it with an anchor thread at the appropriate energy crux then it should work. */
UPDATE dbscript_string SET content_loc3='Wenn wir eine geeignete Sequenz benutzen, sollte es funktionieren. Es werden vor Beginn nur sorgfältige Studien nötig sein.' WHERE entry=2000005173; /* If we use the appropriate sequence we should be ok. Will just take some serious studying before we start. */
UPDATE dbscript_string SET content_loc3='Und nun... Der letzte Schritt!' WHERE entry=2000005178; /* And now... the final step! */
UPDATE dbscript_string SET content_loc3='Es ist vollbracht. Das Pergament ist fertiggestellt. Kehrt nun zu Gorzeeki zurück...' WHERE entry=2000005179; /* I\'m finished. The parchment is made. Now, return to Gorzeeki... */
UPDATE dbscript_string SET content_loc3='Hallo, Charys. Meine Liste ist fertig. Könnt Ihr mir bitte all dies besorgen, besonders die letzte Zutat?' WHERE entry=2000005180; /* Hello, Charys. I have my list, could you get me all of that, especially the last ingredient. */
UPDATE dbscript_string SET content_loc3='Sicher, Paige. Seid nur freundlich.' WHERE entry=2000005181; /* Sure Paige. Just be gentle. */
UPDATE dbscript_string SET content_loc3='Danke, Charys. Komm, Paige, Süße.' WHERE entry=2000005182; /* Thanks, Charys. C\'mon Paige, sweetie. */
UPDATE dbscript_string SET content_loc3='Mami? Kann ich Patzerle streicheln?' WHERE entry=2000005183; /* Mommy? Can I pet Fizzles? */
